INSERT INTO `character_sheet` (`id`, `energy`, `wisdom`, `mind`, `stamina`, `specialty`, `mastery`, `legendary`, `bonus_received`, `current_episode`, `date_created`, `text`, `user_id`) VALUES
(5, 5, 1, 2, 5, 'Бой с меч', 'Харизма', 'Шаманизъм', 1, 3, NULL, '', 1),
(8, 5, 0, 2, 5, 'Бой с меч', 'Стрелба с лък', 'Рефлекс', 1, 1, NULL, '', 2),
(9, 3, 1, 2, 5, 'Бой с меч', 'Рефлекс', 'Шаманизъм', 0, 32, NULL, 'куче', 3);
INSERT INTO `episode` (`id`, `text`) VALUES
(1, 'Ким Ги Су вървеше с отмерени крачки по отъпкания път към Йо Су, родния край на прокълнатата девойка. Беше лято и наситено зелената природа се радваше на редуващи се изобилни дъждове и жарко слънце. Влагата от последният порой се изпаряваше под топлите лъчи и образуваше лека мараня. Уханието на полски цветя бе примесено със специфичния мирис на загниваща растителност. Хуаранг въздъхна и отново прехвърли в ума си разговора с магистрата Канг, който го беше отпратил на тази безумна задача\r\n-	Помогни на стария си приятел, Ги Су! Момчето ми линее и от ден на ден заприличва все повече на призрак! - бе казал магистрата, а си бе помислил „Проклетникът се налива с алкохол денонощно и ме излага жестоко! Ще си загубя и службата и доброто име, с този мой непрокопсаник!\r\n-	Много бих искал да ви помогна магистър Канг - бе започнал оправданието си рицарят хуаранг, но веднага бе срязан\r\n-	Спомни си как преди година, когато бе несправедливо обвинен, че си посегнал на честта на дъщерята на магистър Ли, аз бях този, който помогна на справедливостта да възтържествува! Нима искам много в отплата!\r\n\r\nНа което Ким Ги Су можеше само да отвърне\r\n-	Не, достопочтени и справедливи магистре! - и да добави покланяйки се през стиснати зъби - Ще бъде най - висока чест за мен да помогна на вашия благочестив наследник! - „За чиято благочестивост, както чувам се носят легенди из бордеите на столицата.“ - беше добавил наум\r\n-	Освен това, след като извършиш това благородно и героично дело, поколения куангде ще те възхваляват! - беше додал магистърът, полагайки огромни усилия да не издаде облекчената си усмивка. „Сякаш проклетите, кресливи пъстродрешковци и сега не бълват всевъзможни небивалици по мой адрес!“ - бе си помислил Ги Су покланяйки се още по - ниско, за да не се вижда почервенялото му от гняв лице.\r\n\r\nИ така, той скъсяваше с отмерена крачка разстоянието до задачата си. Да разбере защо всички кандидат-женихи на злочестата, прокълната девойка не доживяват утрото, след като са отправили намеренията си и, стига да е в рамките на възможното и постижимото, да вдигне въпросното проклятие, за да може магистратският син да заяви претенциите си към нея и да спре да унищожава методично и упорито наличния алкохол в столицата и името на баща си заедно с него.\r\n\r\nКак - Ким Ги Су нямаше представа, но беше длъжен да даде най- доброто от себе си. След като така или иначе щеше да стане за смях сред другарите си, можеше поне да завърши обидната мисия с успех.\r\n\r\nНалегнат от мрачно настроение, рязко контрастиращо с красивия пейзаж около него, изведнъж Ги Су чу резки викове и крясъци, които го върнаха в настоящето. <br/>\r\n\r\nИзбери на кой епизод да продължиш: ###2###, ###3### или ###4###.'),
(2, 'Изведнъж, от високите треви на десетина метра пред него се изправиха неколцина дрипави, брадясали и осезаемо нечистоплътни мъже, въоръжени със сопи, брадви, касапски ножове и дори един ръждясал меч. Два нескопосано направени лъка бяха изпънати, насочвайки към него стрели със заострени върхове. Мъжагите полагаха огромни усилия да изглеждат страшно и очевидно се вземаха нaсериозно. Ким Ги Су не желаеше да разваля изградените явно с много сухи тренировки страховити физиономии пред себе си, затова положи усилие и сподави смеха си в шепа, спирайки на място. Страшниците, явно приели този му жест за израз на уплаха, добавиха и нечленоразделни викове към сцената, за да го вцепенят съвсем. Почти успяха защото на хуаранг му се наложи да вдигне и другата ръка към устата си. Един от тях, явно главатарят им, се приближи към него и изръмжа\r\n-	Хайде сега, богаташче, давай насам парите и оръжията, ако ти е мил животът! - останалите също се приближиха и бавно образуваха кръг около него.\r\n\r\nЯвно банда крадци му бяха устроили засада. Ким Ги Су свали ръцетe си като ги държеше колкото може встрани от тялото си\r\n-	Ами, вижте сега момчета, относно парите, не са много, но ще ви ги дам\r\n-	Първо оръжието! - отвърна главатарят. - По - бързо\r\n-	Добре, добре! Само се успокойте! - Ким Ги Су откачи меча си и го хвърли в краката на главатаря. Онзи се наведе и го взе. През това време хуаранг бе взел кесията с парите в едната си ръка и лъка в другата. Хвърли развързаната кесия към лицето на главатаря и той вдигна инстинктивно ръце, за да се предпази от летящите монети.\r\n\r\nАко искаш да използваш умението: Майстор на меча - иди на ###5### Майстор стрелец - иди на ###6### Шаманизъм - иди на ###7###\r\n\r\nАко нямаш тези умения, или не желаеш да ги използваш - иди на ###9###'),
(3, '-	Оставете ме! Проклетници! - чу се изтънял старчески глас\r\n-	Не! Не! Не пергамента! Ще го съсипете! Несретници! За какво ви е? Вие едва ли можете да четете!\r\n\r\nСред бурния смях на група мъже се открои един плътен и дрезгав глас\r\n-	Така е старче, но пък сигурно е ценен. Ще му вземем добри парици\r\n-	Ценен е за мен, не и за вас изкопаеми такива! Дано в следващия си живот се преродите в изтривалки за задници...охххх! -тирадата завърши със жалостив стон\r\n-	Я да млъкваш, дядка, че да не стигнеш по-бързо там, за където си се запътил\r\n-	И вие ще стигнете там, рано или късно, изедници недни, и тогава Буда ще ви съди за изстъпленията ви! Не! Внимателно с колбата!Оххх! Да си счупиш крака дано\r\n-	Ако не млъкнеш ще го счупя в твоите задни части, дъртако! Ким Ги Су изкачи лекото възвишение и стана свидетел на странна и, в друг случай, комична гледка. Шест дрипави мъже, въоръжени с примитивни оръжия бяха наобиколили един възрастен странстващ монах, който кръжеше около тях и размахваше кокалестите си ръце в безуспешни опити да улови някакъв навит на руло свитък, който онези подхвърляха помежду си. Старчето не спираше да проклетисва, с което периодично си спечелваше по един ритник отзад от най - близкия от мъжете до него. Очевидно Ги Су ставаше неволен свидетел на това, как банда пътни разбойници обира възрастен монах. Той не можеше да остане безучастен. Трябваше да се опита да помогне на стареца.\r\n\r\nРазположението им бе в негова полза, тъй като групичката беше с гръб към него, образувайки полукръг около щуращия се монах\r\n-	Върнете ми нещата, глупаци! - крякаше старчето. - Аз съм монах - чародей и дълги години съм изучавал тайните на езотериката. - той се спря и заканително размаха юмрук. - Ако не се вразумите ще стоваря върху ви демони от най-ужасния кръг на Седмия Ад! - разбойниците избухнаха в смях\r\n-	Добре тогава! Сами си го изпросихте! - монахът вдигна ръце към небето и занарежда - Шинаянтра ,махарава, омнавашивая.\r\n\r\nАко искаш да използваш умението: Майстор на меча - иди на ###13### Майстор стрелец - иди на ###14###\r\n\r\nШаманизъм - иди на ###15###\r\n\r\nАко нямаш тези умения, или не желаеш да ги използваш, запиши кодова дума “кражба” и иди на ###13###'),
(4, 'Ким Ги Су чу пред себе си детски смях и викове, както и кучешко квичене, което прерасна в приглушен лай и отново се извиси до вой. Хуаранг забърза крачка и изкачи възвишението пред себе си. Пред очите му се разкри една от най- ужасяващите гледки, които бе виждал в живота си. Банда хлапетии бяха приклещили едно дръгливо псе в пространството между две скали и го обстрелваха с прашки, заредени със ситни камъчета. Бедното животно се щураше насам - натам като пощръкляло, в безуспешни опити да избегне поредния залп, а всеки път за бягство му бе отрязан, тъй като жестоките хлапета го бяха обградили в полукръг. На всичкото отгоре, бяха успели да завържат опашката на горкото псе за един от задните му крака с парче канап, за да засилят безпомощната му агония. Справедлив гняв изпълни цялото същество на хуаранг при вида на тази жестокост и той забърза към малките садисти, за да въздаде правосъдие\r\n-	Хей, вие! - изкрещя той. Нехранимайковци такива! Я веднага престанете! Защо измъчвате животното!\r\n\r\nДецата му отвърнаха със смях и заляха с нов залп кучето, което се бе свило примирено и очакваше съдбата си\r\n-	Я, да се разкарате веднага! Сега ще ви скъсам ушите! - побесня Ги Су.\r\n\r\nВ отговор, едно от по-едрите хлапета се извърна към него, прицели се и го замери с камък от прашката си. Хуаранг с лекота избегна и без това неточния изстрел, но това окончателно преля чашата на търпението му и той се впусна в бяг към калпазаните. Те от своя страна, разбрали, че нещата никак не отиват на добре за тях, тътриха да бягат към селото\r\n-	Върнете се, ситни проклетници! Не бягайте! Ще разбера кои сте и ще се погрижа да си получите заслуженото! - Ги Су дотърча с викове до мястото, на което се бе разиграла жестоката случка. Кучето се бе понадигнало и наблюдаваше спасителя си със смесица от учудване и благодарност\r\n-	Еишшшш! - изруга за последно хуаранг и се обърна към кучето, което проскимтя умолително и се придвижи към него. Ги Су се наведе и развърза канапа, пристягащ опашката и крака му. Благодарното животно заразмахва освободената си опашка и започна да ближе ръката на рицаря\r\n-	Добре, добре! Няма защо да ми благодариш! Хайде, сега си заминавай по пътя и друг път стой далеч от хлапетии. Хуаранг се изправи и пое по пътя си, но след няколко крачки спря и установи, че четириногото се мъкне подире му\r\n-	А, не! Хайде, заминавай си! - кучето не помръдна. - Марш! - псето го изгледа гузно след което отново се нахвърли да го ближе по ръцете\r\n-	Оооо, престани! Какво да те правя! Имаш късмет, че не ям кучешко! Да знаеш, че няма да те храня и ще се оправяш сам! Пък като искаш, мъкни се след мен! Да видим докога ще издържиш! - животното заскимтя и даже препика тревата под себе си от щастие\r\n-	Само ти ми липсваше! Сега сигурно ще се прочуя и като Повелителя на Кучетата, или Закрилника на Четириногите - изсумтя той и двамата поеха към село Йо Су.\r\n\r\nВече си имаш куче. Иди на ###12###.'),
(5, 'В това време Ги Су вече бе вихър от движение. Хвърли се към меча си, превъртя се на земята през рамо и заби дръжката в корема на главатаря. Замахна нагоре и мечът излетя високо във въздуха, привличайки погледите на застиналите от изненада бандити. В същото време, той вече бе вихър от движение, избивайки лъковете от ръцете на стрелците с празната ножница преди онези да могат да пуснат стрелите си. Претърколи се назад, протегна се театрално и мечът падна в ръката му. С бързо движение на китката подкъси сплъстената брада на главатаря с два сантиметра, мръдна острието леко напред и го опря в гърлото му. Пишман бандита бе видимо посивял от страх, въпреки солидния пласт мърсотия покриващ лицето му\r\n-	Е, приятелю, нали искаше парите и оръжието ми - получи ги. Искаш ли още нещо? - попита Ким Ги Су без следа от веселост в гласа си.\r\n\r\nИди на ###96###.'),
(6, 'В същото време Ги Су светкавично измъкна стрела от колчана си, клекна на коляно и стреля. Стрелата му разцепи на две летящата към него вражеска стрела и отнесе шапката от главата на стрелеца. Претърколи се и повтори същото упражнение с втория стрелец. Изправи се и зареди третата стрела на педя от лицето на втрещения главатар. Очите на онзи се събраха при вида на зловещото острие на стрелата\r\n-	Този вид стрели се казват „таран“ - осведоми го рицарят\r\n-	Наименованието им идва от тежестта на острието - само то тежи двеста грама. Такава стрела може да пробие подсилена дървена порта и да остави дупка в нея, през която да си провреш ръката. Представи си ако ти, или приятелчетата ти мръднете, какво ще се случи с главата ти. Е, нали искаше парите и оръжието ми - получи ги. Искаш ли още нещо? - попита Ким Ги Су без следа от веселост в гласа си.\r\n\r\nИди на ###96###.'),
(7, 'През това време Ги Су бе извършил едно простичко заклинание, което умееше - за сгъстяване на мъгла. За секунди цялата вдигаща се пара от мократа земя наоколо се събра в радиус на десет метра около него, образувайки гъста, млечна пелена, в която бе трудно човек да различи ръцете пред лицето си. Можеше да задържи мъглата само за десетина секунди, но и те му бяха достатъчни. За това време той с няколко точни юмручни удара обезоръжи стрелците, измъкна меча си от ръцете на втрещения главатар, извади го от ножницата и когато мъглата се вдигна толкова бързо, колкото беше дошла, онзи усети върха на меча до гърлото си\r\n-	Магия\r\n-	Да. Точно така! Е, нали искаше парите и оръжието ми - получи ги. Искаш ли още нещо? - попита Ким Ги Су без следа от веселост в гласа си.\r\n\r\nИди на ###96###.'),
(8, 'Ако точките ти за познание са три или повече - иди на ###68###. В противен случай - продължи на ###100###.'),
(9, 'В това време Ги Су вече бе вихър от движение. Хвърли се към меча си, превъртя се на земята през рамо и заби дръжката в корема на главатаря, след това го завъртя в полукръг и с набраната инерция го запрати към двамата стрелци. Самия той използва въртящия момент и връхлетя върху останалите разбойници, които разбрали, че са направили сериозна грешка, дадоха най - доброто от себе си, за да движат краката си колкото се може по - бързо, бягайки през глава в различни посоки. Ги Су се обърна към повалените от главатаря стрелци, но и тримата вече се изправяха панически и къде на два, къде на четири крака, тътриха да бягат. Ги Су събра разпръснатите си монети и продължи по пътя си. Пишман разбойниците щяха дълго време да премислят преди отново да се мярнат насам.\r\n\r\nИди на ###12###.'),
(10, 'Главатарят извади малка, здраво зашита, издута кожена торбичка. -	Даде ни го един добър монах. Ако го хвърлите силно и се удари в нещо твърдо се чува „БУУУМ!“ и нещото изчезва в облак прах и дим! Ето заповядайте! - бандитът държеше торбичката сякаш е змия.\r\n\r\nХуаранг я взе и каза\r\n-	Е, благодаря ви момчета, а сега марш оттук\r\n-	Да, да, благородни господине, тръгваме си. Довиждане на вас ъъъм, така де, сбогом. На добър час и успех във всичко... -дърдореха бандитите докато ситнеха заднешком. Отдалечиха се на двадесетина метра ходом, след което хукнаха да бягат през глава. Ким Ги Су най - после прихна и се посмя добре няколко минути,\r\n\r\nслед което продължи пътя си. Запиши кодова дума „жар“.Иди на ###12###.'),
(11, 'Главатарят извади малка, неугледна колбичка\r\n-	Даде ни го един добър монах. Прочиства и избистря ума тая течност - и за доказателство отпи глътка от нея\r\n-	Е, благодаря ви момчета, а сега марш оттук\r\n-	Да, да, благородни господине, тръгваме си. Довиждане на вас ъъъм, така де, сбогом. На добър час и успех във всичко... -дърдореха бандитите докато ситнеха заднешком. Отдалечиха се на двадесетина метра ходом след, което хукнаха да бягат през глава.\r\n\r\nКим Ги Су най - после прихна и се посмя добре няколко минути, след което продължи пътя си.\r\n\r\nУвеличи точките си за Съзнание с една. Иди на ###12###.'),
(12, 'Ким Ги Су пристигна в селото в късния следобед. Сгъстяваща се мъгла бе започнала да скрива покривите на петдесетината къщи на местните обитатели. Въздухът бе толкова влажен, че сякаш лепнеше по ръцете и лицето на хуаранг и бе наситен с уханието на зелен чай. Ги Су погледна към двата хълма, застинали като мълчаливи пазители на поселището. Те бяха грижливо терасирани и засети с ароматното храстовидно растение, чийто добив бе основния поминък на местните.\r\n\r\nГледката на зелените стъпала на възвишенията, които изчезваха в млечнобялата пелена от мъгла, създаваше странно чувство на мистичност и безвремие, сякаш там, където върховете на хълмовете се губеха започваше безкрая на всемира и края на света. Този пейзаж бе едновременно красив и по своему заплашителен, противопоставяйки края на познатото и реалното с началото на непознатото и забуленото в тайнственост. Хуаранг остана загледан във възвишенията няколко минути, изпълнен с противоречивите чувствата на мир и безпокойство.\r\n\r\nЧу приближаващи се стъпки, извърна се и видя минаваща наблизо старица\r\n-	В мир ли сте? - поздрави учтиво Ги Су\r\n-	Отиде си мира от село Йо Су, момко! - отвърна тя. - Откак онази взе да омайва момците ни, няма мир за нас! Вчера погребахме и Джи Уон Хян. И него затри. Прокълнати сме ние и няма мир за нас! Върви си страннико и не се връщай тук повече! - гласа й заглъхваше докато тя отмина и очертанията й започнаха да се губят в бялата пелена.\r\n\r\nКим Ги Су беше твърде втрещен от отговора на старицата, за да може да отвърне нещо. Не беше очаквал радушно посрещане, но подобно зловещо предупреждение наистина го потресе. Нещо определено не беше наред в село Йо Су. Той постоя известно време сам в мъглата, след което реши да...\r\n\r\nпоразпита наоколо иди на ###20###\r\n\r\nогледа покрайнините на селото - иди на ###21###\r\n\r\nсе насочи право към къщата на девойката - иди на ###22###'),
(13, 'В този момент Ги Су връхлетя върху най-близкият разбойник, който бе вдигнал сопата си, с намерението да цапардоса по главата устатия монах. Изрита го в свивката на коляното и онзи се просна по гръб с рев, повече от страх отколкото от болка. Ким Ги Су с два бързи маха на меча си изби от ръцете на сащисаните бандити притежанията на монаха и с още два обезоръжи оставащите двама.\r\n\r\nИди на ###97###.'),
(14, 'В този момент Ги Су стреля по най-близкият разбойник, който бе вдигнал сопата си, с намерението да цапардоса по главата устатия монах. Уцели го в свивката на коляното и онзи се просна по гръб с рев, повече от страх отколкото от болка. Ким Ги Су с два бързи изстрела изби от ръцете на сащисаните бандити притежанията на монаха.\r\n\r\nИди на ###97###.'),
(15, 'В този момент Ги Су направи едно простичко заклинание, което умееше - предизвика мъгла над групата мъже пред него. Можеше да я задържи само за около десетина секунди, но и те му бяха достатъчни.\r\n\r\nИди на ###13###.'),
(16, 'Можеш да зададеш допълнителен въпрос. -	Бих искал да науча повече за: Странната шаманка - иди на ###62###.\r\n\r\nХьо Са Гуа - иди на ###63###. Хьо Уон Джин - иди на ###64###.'),
(17, 'За съжаление, онези идиоти, Буда дано ги дари с хиляда поредни прераждания като червеи, ми отмъкнаха всички притежания и нямам дар, с който да изкажа благодарността си затова, че спаси живота ми. Освен ако, не пожелаеш да отделиш малко от времето си, за да споделя с теб някои от тайните на езотериката и свръхестественото, които съм натрупал с годините, съвсем като за начинаещи, разбира се!\r\n\r\nКодексът на честта, за съжаление задължаваше Ким Ги Су да се съгласи и в следващия половин час му се наложи да изтърпи отнесената лекция на монаха. Длъжен бе да признае, обаче че научи доста неща. След като приключи старчето му благодари още веднъж и тръгна по пътя си, проклетисвайки под мустак разбойниците, а хуаранг пое към селото.\r\n\r\nУвеличи точките си за познание с еднаи и иди на ###12###.'),
(18, '-	В знак на благодарност, че спаси живота ми ще ти дам ето този свитък, който проклетниците се опитаха да задигнат! - заяви монахът с не особено голям ентусиазъм. - Много е ценен, защото съдържа няколко основополагащи трактата по езотерика и ти го давам, защото знам, че ще оцениш стойността му\r\n-	Не мога да приема такъв прескъп дар от теб, щедри монахо! Не съм достоен за такава мъдрост! - отвърна с особено голям ентусиазъм Ким Ги Су, тъй като изобщо не държеше да става новият собственик на изписания с отнесени и суховати трактати свитък. Още по - малко пък, да оцени достойнствата му и да го чете\r\n-	Ще го вземеш! - тросна се старчето. - Иначе ще имам карма към теб! Аз и без това знам всяка дума от свитъка наизуст! Да не би да искаш да попречиш на следващото ми прераждане\r\n-	Ни най-малко, благочестиви монахо! - отвърна, ще, не ще, Ги Су. -	В такъв случай ще е най - висока чест за мен, да приема този ценен източник на мъдрост и познание от теб и дано Буда да даде в следващия си живот да се преродиш в бодхисатва, заради добрите ти дела в този! - завърши той и церемониално пое свитъка с две ръце от монаха\r\n-	Дано Буда бди над успеха на всичките ти начинания, смели хуаранг! - също така церемониално отвърна монахът. След което му благодари още веднъж и тръгна по пътя си, проклетисвайки под мустак разбойниците, а Ким Ги Су пое към селото.\r\n\r\nУвеличи точките си за познание с ###2### и иди на ###12###.'),
(19, '-	В знак на благодарност, че спаси живота ми ще ти дам ето тази колба, която проклетниците се опитаха да отмъкнат! - заяви старецът с не особено голям ентусиазъм. - Това питие е направено от съчетание на множество тайни съставки и прочиства съзнанието и ума! Давам ти го, защото знам, че ти си достоен да оцениш стойността на този дар\r\n-	Не мога да приема толкова ценен дар, монахо - отвърна с особено голям ентусиазъм Ким Ги Су, защото никак не му се щеше да разнася отвари със странен произход, още по - малко пък, да ги пие\r\n-	Можеш! - тросна се монахът. - Иначе ще имам карма към теб! Да не би да искаш да попречиш на следващото ми прераждане\r\n-	В никакъв случай, благочестиви и щедри монахо! - отвърна хуаранг, ще, не ще. - За мен е чест да приема този дар и дано Буда да даде в следващия си живот да се преродиш в бодхисатва! - завърши той и церемониално пое с двете си ръце глинената колбичка\r\n-	Нека Буда бди над успеха на начинанията ти, смели хуаранг! - отвърна също толкова церемониално старецът\r\n-	А сега, пий\r\n-	Амми, аз всъщност мислех да я запазя в случай на нужда... -	Пий! - кресна старикът.\r\n\r\nГи Су измъкна тапата на глинената колбичка, задържа дъха си, събра цялата си смелост и изля съдържанието й в устата си. Единствено дълго тренираните му самообладание и воля на рицар - хуаранг го спряха да не изпразни червата си в краката на монаха. Течността имаше вкус на умрели преди седмица, изсушени плъхове\r\n-	Усещаш ли прилива, синко? - попита монахът с благоговеен глас. -	Мхмм! - успя някак да отвърне Ги Су, понеже дори и прилив все още да не усещаше, поривът все още бе твърде силен и не му се рискуваше да отваря устата си\r\n-	Усещаш ли как съзнанието ти е вече с крачка по - близо до нирвана\r\n-	Ммммм\r\n-	Е, благодаря на съдбата, че ни срещна и ме отърва от онази напаст! Остани със здраве и дано Буда подкрепя десницата и умът ти - каза старчето и се приготви да потегля по пътя си\r\n-	Мхммм - отвърна хуаранг.\r\n\r\nМонахът още веднъж му благодари и се запъти проклетисвайки тихо разбойниците под мустак, а Ким Ги Су пое по пътя си към село Йо Су. Скоро отвратителният вкус в устата му се разнесе и той с изненада установи, че съзнанието му наистина е по - бистро. Подозираше, че ефектът се дължи не толкова на съставките на отварата, колкото на ужасяващия й вкус.\r\n\r\nУвеличи точките си за съзнание с една и иди на ###12###.'),
(20, 'Ким Ги Су реши, че преди да започне изпълнението на задачата си е добре да поразпита из селото и да се опита да получи малко повече информация за случващото се тук. Имаше няколко възможни варианта и той обмисляше откъде да започне:\r\n\r\nМожеше да потърси кметството, където да представи официалния документ от магистъра, удостоверяващ официалната му мисия „да стигне до дъното на криминалните изстъпления, петнящи името на невинна девойка“ и да зададе въпроси за случващото се на местният представител на кралската власт (освен ако вече не го беше правил)- иди на ###52###.\r\n\r\nОт друга страна можеше да потърси и местната кръчма, тъй като тези места винаги бяха извор на информация. Просто трябваше да я отсее от купчината слухове(освен ако вече не го беше правил) - иди на ###53###.\r\n\r\nА, може би, беше най - добре да потърси местната шаманка. В крайна сметка, ако наистина се случваше нещо, в което бяха намесени нечисти сили, тя трябваше да е поне донякъде наясно с естеството на случващото се (освен ако вече не го беше правил)- иди на ###54###.\r\n\r\nА ако си беше променил мнението можеше да огледа покрайнините на селото - иди на ###21###.\r\n\r\nИли да се отправи към дома на прокълнатата девойка - иди на ###22###.'),
(21, 'Можеш да изследваш чаените хълмове, освен ако вече не си го направил- иди на ###65###.\r\n\r\nАко нямаш кодова дума „гривна“, можеш да отидеш до близкото езеро, освен ако вече не си бил там - иди на ###66###.\r\n\r\nАко нямаш кодова дума „вода“, можеш да посетиш местното гробище - иди на ###67###.\r\n\r\nАко си бил на тези места, можеш да събереш информация от жителите в селото ###20###, или да се насочиш направо към къщата на прокълнатата девойка ###22###.');
INSERT INTO `episode` (`id`, `text`) VALUES
(22, 'Ким Ги Су сравнително лесно откри къщата на набедената за прокълната девойка. Всеки втори, който попита просто му посочваше посоката и бързо го отминаваше. Останалите просто го отминаваха, като забързваха крачка дотолкова, доколкото им позволяваше приличието. Къщата беше голяма за стандартите на село Йо Су и бе явно, че е собственост на заможен човек. Все още личеше, че доскоро е била добре поддържана, но неоплевената цветна градина и необраните овошки, отрупани със загниващ плод издаваха, че грижите за нея са останали на по - заден план. Сякаш стопанка или нямаше, или беше заболяла. Имаше няколко по - малки къщи около нея, но по здраво залостените прозорци и врати, Ги Су разбра, че няма да може да разпита съседите. Той се приближи към портата и похлопа. (Ако Ги Су има куче, животното надава гневно ръмжене щом той доближава вратата, което прераства в скимтене и виждайки, че хуаранг е твърдо решен да влезе вътре, побягва.) Отвътре излезе едър мъж, около петдесетте с посребряваща коса, мустаци и брадичка, отвори портата и попита сърдито\r\n-	Какво обичате\r\n-	В мир ли сте? - поздрави Ги Су, не изчака обратен поздрав, какъвто се съмняваше, че ще последва и продължи. - Казвам се Ким Ги Су. Хуаранг Ким Ги Су\r\n-	Аз съм Хьо Уон Джин, приятно ми е. С какво мога да ви бъда полезен, господине? - тона на мъжа се поукроти леко\r\n-	Всъщност, господин Хьо, аз съм тук, за да се опитам да ви помогна - онзи повдигна учудено вежди. - Виждате ли, до столицата достигнаха слухове, че над прекрасната ви дъщеря тегне някакво проклятие и че този слух до такава степен е завладял областта, че има опасност от брожения. По този повод съм изпратен тук, за да разнищя тази история и да успокоя духовете на местното население. А ако красивата ви щерка наистина е под омаята на проклятие, то задачата ми е да направя всичко необходимо, за да й помогна.\r\n\r\nХьо Уон Джин примигна няколко пъти и изражението му се смени от подозрително, през удивено до озадачено\r\n-	Влезте да поговорим вътре уважаеми хуаранг - покани го най - сетне той.\r\n\r\nВлязоха в добре обзаведената гостна и Хьо нареди на един прислужник да донесе чай. Двамата поговориха надълго за незначителни теми и Ги Су с удоволствие отбеляза, че домакинът е ерудиран и интересен събеседник. След като се посмяха добре на поредната шега, Хьо най - сетне зададе въпроса, който явно го терзаеше от самото начало\r\n-	Ги Су, кажете, моля ви, каква е истинската причина за вашето посещение? Вярвам ви напълно на казаното дотук, видях и назначението ви от магистъра и съм благодарен за помощта, която сте готов да окажете, но нека бъдем честни - колкото и да е прекрасна моята Са Гуа и колкото и злочеста да е съдбата й - само това едва ли е причината за идването ви.\r\n\r\nХуаранг реши, че човекът заслужава малко откровеност и му призна\r\n-	Ами, истината е, че сина на един висш чиновник е разбрал за чудната красота на дъщеря ви и желае да я ухажва. Само че е чул слуха за тегнещото проклятие и не смее да предяви претенциите си, докато не е сигурен, че такова няма, или ако има е премахнато\r\n-	Аха - кимна разбиращо Хьо Уон Джин, след което се умълча. Тъй като бе дошло време за вечеря, той повика прислужника и го помоли да сервира храната и да помоли дъщеря му да се присъедини. Не след дълго Хьо Са Гуа срамежливо влезе в стаята и за миг Ги Су спря да диша. Слуховете за нея дори не се доближаваха до истината. Девойката бе наистина прелестна. Нито прашинка грим не загрозяваше изваяното й лице със сочни, малки устни, дълбоки черни очи, тънки вежди каквито никой калиграф не би могъл да изрисува и малко, леко чипо носле. Тя вдигна поглед едва колкото да погледне бегло Ги Су и веднага сведе очи\r\n-	Добър вечер! В мир ли сте? - изрече тя с дълбок и звънък, женствен и чувствен глас, който никак не подхождаше на възрастта й и накара стаята да стане по-светла\r\n-	Д... Добър вечер! А вие? - отвърна той и за малко да се изчерви. -	Миличка, това е хуаранг Ким Ги Су. Дошъл е чак от столицата,\r\n\r\nза да се опита да ти помогне\r\n-	О, татко! Нима моята злочеста съдба е разбунила духовете дори там? Горката аз - проплака тя и сърцето на Ги Су също заплака\r\n-	Скоро всичко ще се нареди, мила моя! Господин Ги Су ще се погрижи\r\n-	А ако не беше ти, скъпи татко, какво ли щеше да е сега? - попита тя риторично\r\n-	Са Гуа, моля те! Не започвай пак\r\n-	Аз нищо не започвам татко! Аз само страдам. Вие с майка започнахте всичко\r\n-	Са Гуа! - сопна се гневно баща й. - Ако не престанеш, ще останеш без вечеря\r\n-	Извинете ни, уважаеми Ги Су. Дъщеря ми все още смята, че аз имам вина за смъртта на майка й - моята обичана съпруга и че това има някаква връзка с проклятието й. Смята, че съм предизвикал някаква зла сила да я прокълне.\r\n\r\nДевойката не каза нищо но хвърли един таен и изпълнен с предизвикателство поглед към Ги Су. Той се изправи, със замах извади меча си и каза\r\n-	Аз, хуаранг Ким Ги Су, се заклевам, че докато дишам, ще търся и ще открия начин да отърва тези хора от злочестината им. Нека това бъде моето обещание и предизвикателство към която и да е зла сила имаща пръст в това!\r\n\r\nСлед това театрално представление бащата гледаше като зашлевен, а девойката странно защо се сви, след което тихо каза\r\n-	Внимавайте какво говорите, хуаранг! Може би няма да е по силите ви да изпълните обещанието си\r\n-	Напротив! - отвърна Ги Су и седна обратно.\r\n\r\nТримата вечеряха мълчаливо, а Ги Су изпитваше затруднения да не гледа към приказно красивото създание пред него. След вечеря се разделиха учтиво и Ги Су каза, че ощe от утре ще започне да търси начин да разнищи ситуацията. Слугата го отведе до стаята за гости - удобно и просторно помещение с прозорци към вътрешната градина с изкуствено езерце.\r\n\r\nТой се приготви да си ляга, уморен от дългия ден, когато на вратата на стаята тихо се почука\r\n-	Влез! - каза той, като мислеше, че отново е слугата, който е забравил нещо. За негова изненада това беше Са Гуа. Беше облечена в красив копринен *ханбок и носеше в ръце малък поднос\r\n-	Беше ме страх да остана сама в стаята си. - каза тя, след като стана очевидно, че Ги Су няма да може да накара устата си да заговори скоро. - Затова реших да ти правя компания известно време\r\n-	Са Гуа, не е прилично! Какво ще си помисли баща ти\r\n-	Той спи. Пък и какво да си помисли - просто ще поговорим, ако нямаш нищо против, разбира се\r\n-	Ами, не! Съвсем не! Заповядай!\r\n\r\nДевойката влезе и остави подноса на малката масичка в стаята. -	Донесох малко вино - за да разсее моя страх, а на теб да даде смелост! - тя му подаде едната чашка.\r\n\r\nКим Ги Су погледна още веднъж протегната към него красива чашка от зеленикав порцелан, вдигна поглед към невинните и доверчиви очи на красавицата и се замисли за миг. Може би всичко беше преувеличено. Все пак това беше просто чаша вино. Какво толкова щеше да стане. След още миг той реши...\r\n\r\nда изпие чашата - иди на ###41###.\r\n\r\nда откаже питието - иди на ###46###.'),
(23, 'Докато се луташе из околностите не съвсем желания, но верен спътник на Ги Су изведнъж нададе бесен лай, с който го стресна не на шега и се впусна в яростен бяг някъде напред. Ги Су примига изненадан секунда - две, след което изруга вбесен и тръгна по посока на настойчивия лай на псето\r\n-	Проклето псе! Къде хукна, така! Я да се връщаш тука! Ако си мислиш, че ще те гоня, си сбъркал! Какво те прихваща! Я да... -Ги Су млъкна по средата на изречението и зяпна от почуда.\r\n\r\nKучето беше открило добре прикрита дупка в подножието на един от хълмовете и лаеше побесняло на входа й. Тя бе широка колкото да се провре човек през нея. Ги Су реши да се довери на инстинкта на четириногото и да изследва вътрешността й. Иди на ###27###.'),
(24, 'Ги Су се луташе безцелно наоколо, разчитайки на изострените си от шаманстичните познания сетива да го насочват. Краката му го отведоха отвъд хълма, където се намираше гробището на селото. Изведнъж смътно усещане за нещо зло, за нещо нередно го привлече надясно към подножието на възвишението. Той последва дирята и скоро се озова пред добре замаскиран вход на дупка широка, колкото да се провре човек през нея. Източникът на усещането идваше някъде отвътре. Той реши да се довери на нюхът си и реши да изследва вътрешността й. Иди на ###27###.'),
(25, 'Продължи на епизод ###70###.'),
(26, 'Избери на кой епизод да продължиш - ###29### или ###24###.'),
(27, 'Ким Ги Су пропълзя наполовина в дупката и повече усети, отколкото видя, че метър по - нататък процепа се раширява. Изпълзя обратно, извади огнивото си и запали малка лоена лампа, която винаги носеше със себе си и се напъха обратно в отвора. След метър и половина пръстта под него бе заменена от скала и скоро той излезе от дупката, за да се озове в не голяма пещера под хълма. На мъждукащата светлина на лампата той огледа помещението. То бе високо около два метра и дълго и широко около три, а в центъра му имаше голям объл камък. Приближи се до камъка, който странно защо му напомняше на олтар. Около и по него забеляза следи от пепел и сажди. Изпълнен с подозрение той реши да потърси други следи из пещерата.\r\n\r\nАко имаш умението „Шаманизъм“ или „Късмет“ и искаш да го използваш - иди на ###31###.В противен случай продължи на ###33###.'),
(28, 'Връхлетя го вихър от спомени, които определено не бяха негови. Видя замъгления образ на девойка, толкова красива, че не можеше да се осмели да погледне към лицето й. Пулсът му се ускоряваше само от присъствието й. Той знаеше че живее само за щастието да усети как усмивката разцъфва на устните й.\r\n\r\nПоследва образ на строг мъж, от който трябваше да поиска разрешение за нещо. Не знаеше какво, но трепереше от ужас при мисълта, че може да му бъде отказано. Живота му зависеше от това благоволение. После дойде миг на всепоглъщащо шастие от полученото съгласие. Това беше най-щастливия миг в живота му.\r\n\r\nСлед това беше нощ. Разхождаше се из покрайнините на селото, защото не можеше да заспи от вълнение и с нетърпеливо очакваше настъпването на утрото. Тогава я видя да стои сама на брега и да му маха. Той затича към усмивката, която продаваше смисъл на съществуването му. Можеше почти да я докосне.\r\n\r\nПосле дойде звярът. Той замахна и с ноктите си откъсна половината му лице. Болката беше неописуема. Но не можеше да се сравни с болката, която изпита, когато ноктите се впиха в торса му и изтръгнаха черния му дроб за секунда. Агонизираше. Последния удар го запрати в езерото. Не можеше да диша. Умираше. Заради усмивка, която никога повече нямаше да види.\r\n\r\nПосле дойде мрака и със себе си доведе и скръбта.\r\n\r\nХуаранг отвори очи и видя че лежи сам на брега на езерото.От призрака нямаше следа и той започваше да се чуди, дали преживяването му беше истинско.\r\n\r\nЗапиши кодова дума „вода“. Увеличи точките си за познание с една.\r\n\r\nТой се изправи и реши да...\r\n\r\nСе върне в селото и да поразпита местните - иди на ###20###. Изследва покрайнините - иди на ###21###.\r\n\r\nСе насочи право към къщата на прокълнатата девойка - иди на ###22###.'),
(29, 'Ги Су скита цял час из покрайнините на селото, но освен красивата природа, не успя да намери друго, което да използва в разследването си. Дори вътрешното усещане на мир, което будеше в него дивната гледка бе помрачено от ситния дъждец, просмукващ се през дрехите му. С нежелание той призна поражението си и с недоволство, че е пропилял един час от времето си, се запъти обратно към селото. Когато наближи реши да:\r\n\r\nПоразпита местните жители, освен ако вече не го беше направил - иди на ###20###.\r\n\r\nСе насочи към къщата на прокълнатата девойка - иди на ###22###. Ако нямаш кодова дума „гривна“, можеш да отидеш до близкото езеро, освен ако вече не си бил там - иди на ###66###.\r\n\r\nАко нямаш кодова дума „вода“, можеш да посетиш местното гробище - иди на ###67###.'),
(30, 'За да вземеш от мъртвите, първо трябва да дадеш. Ги Су си спомни старата поговрка и остави монета на земята, преди да вземе гривната\r\n-	Виждам че знаеш как да почиташ мъртвите и познаваш ритуалите, хуаранг.\r\n\r\nГласът го стресна и той се завъртя с ръка на меча си. -	Кой си ти?\r\n\r\nИди на ###69###.'),
(31, 'След упорито търсене, ровене и опипване по пода на пещерата, по време на което разчиташе повече на нюха и късмета си отколкото на мъждивата светлина Ги Су откри няколко странни неща. В единия край на пещерата откри плитко заровени, натрошени кости, които бе почти сигурен, че са човешки. В противоположния, също така плитко зарити, намери полуизгнили парцали от дрехи накъсани на парчета.\r\n\r\nВиж оценката си за познание. Ако е по - голяма от ###2### - иди на ###32###. Ако не - иди на ###33###.'),
(32, 'Макар да не успя да открие нищо повече, Ги Су беше убеден, че това място е използвано за извършване на демоничен ритуал. Най -вероятно за метаморфоза.\r\n\r\nЯвно бе, че който и да бе последният посетител тук, се беше постарал добре да почисти след себе си, макар и не дотам успешно. С ускорен пулс и нащрек Ги Су напусна помещението обратно през тясната дупка.\r\n\r\nПрибави две точки Познание. Запиши кодова дума „пещера“. Ако имаш кодови дума „отвара“ или „магия“ - иди на ###29###. Ако не иди - на ###35###.'),
(33, 'Макар да не успя да открие нищо повече на мъждивата светлина, Ги Су беше убеден, че това място е използвано за някакъв нечестив ритуал. За съжаление без допълнителни следи не можеше да определи какъв.\r\n\r\nЯвно който и да бе последният посетител тук, той се беше постарал добре да почисти след себе си. С ускорен пулс и нащрек Ги Су напусна помещението обратно през тясната дупка.\r\n\r\nПрибави една точка Познание. Запиши кодова дума „пещера“. Ако имаш кодови дума „отвара“ или „магия“ - иди на ###29###. Ако не иди - на ###35###.'),
(34, 'Излизайки от пещерата Ги Су откри, че ситния дъжд е спрял, а мъглата се е сгъстила дотолкова, че му беше трудно да вижда ясно на повече от няколко метра напред. Изгаси лоената лампа и я прибра. Над местността цареше мъртвешка тишина. Дори птичите песни бяха секнали, сякаш животинките, притаени, очакваха отминаването на някаква невидима опасност.\r\n\r\nТой тъкмо се готвеше да се връща в селото, когато чу пред себе си провлачени стъпки и приглушено мърморене. Някой се приближаваше насам. Ги Су се притаи зад един доста голям камък, който му осигуряваше добра гледка към посоката, от която се приближаваха стъпките и зачака. Може би изпадаше в прекалена предпазливост, но никой не бе загубил, защото е бил прекалено предпазлив.\r\n\r\nСкоро от гъстата мъгла изплува образът на прегърбена и накуцваща старица, която си говореше нещо под нос.\r\n\r\nАко имаш куче - иди на ###37###. Ако не - на ###95###.'),
(35, 'Избери на кой епизод да продължиш - ###29### или ###34###.'),
(36, 'Въпреки гнева си, Ги Су успя да подтисне порива си да се спусне с изваден меч и да посече изчадието. Не знаеше много за кумихо, но едно помнеше от страшните истории, разказвани за нея - не можеше да бъде наранена от обикновено оръжие. Затова той се отчужди от емоциите, които бушуваха в него и реши да наблюдава действията на демона. Може би те щяха да подскажат целта му и как може да бъде прокуден. Докато той се бореше със себе се, кумихо се приближи до трупа на кучето, разкъса гърдите му и погълна сърцето на животното. След това сякаш се окопити, огледа се бясно, погледа и премина за миг през мястото, където се беше притаил Ги Су, но отмина\r\n-	Кяф! Псето миришеше на човек. Някой май се е навъртал тъдява! Кяф! Поредният глупак, който да ми даде душата си! -след това лисицата - демон подви деветте си опашки, върна се към разпарцаливените дрехи и започна да рови из тях. Скоро измъкна от там нещо гладко, лъскаво и бяло, хвана го в лапите си и започна да го оглежда оттук - оттам\r\n-	Кхъ, кхъ! Поочукало се! Ще трябва да го пооправя! - взе го в уста и с един скок се приземи на един голям и плосък камък срещу хуаранг. Взе отново предмета в лапите си и започна да го потърква в повърхността на камъка, като изричаше неразбираеми за Ги Су инкантации от странни думи, лисичи вой и звуци невъзможни за произнасяне нито от човек, нито от животно. След малко нахлупи предмета на главата си и Ги Су с изумление установи, че това е човешки череп\r\n-	Кяф! Не е съвсем по мярка! Псето го нащърбило лошо. Още малко, още малко! - последваха пак същите нечовешки заклинания, докато лисицата търкаше черепа в камъка. При всяко потъркване, сухата кост издаваше специфичен скърцащ звук, който сякаш пазеше в себе си предсмъртния писък на жертвата. Кумихо отново нахлупи черепа на главата си, изрече няколко кратки остри звука, образът и се замъгли и на нейно място се появи накуцващата, отвратително грозна старица\r\n-	Така, така! Много добре! Още малко и ще мога да захвърля облика на тази отвратителна старица! - мърмореше си тя под нос, докато вървеше към входа на пещерата. Намъкна се вътре с изненадваща пъргавина и Ги Су много предпазливо се доближи до входа на пещерата, като напрягаше слуха си\r\n-	Я да видим сега що за вкус ще има душичката на поредния кандидат - жених? Кяф, кяф!\r\n\r\nГи Су изчака малко и много внимателно надникна в дупката. За негова изненада нещо осветяваше вътрешността на пещерата. Старицата - кумихо бе застанала пред олтара с гръб към хуаранг, а на самия олтар беше поставила някакво голямо колкото жълъд топче. От предмета струеше мека светлина, която сякаш се вливаше в устата на демона.\r\n\r\nБързо се отдръпна и използвайки гъстата мъгла се впусна в тих бяг обратно към селото.\r\n\r\nОчевидно съществото използваше девойката, която уж лекуваше, за да се сдобива с жизнената енергия на душите на кандидат - женихите.\r\n\r\nДобави две точки Познание. Запиши кодова дума „шаман“. Въоръжен с това знание Ким Ги Су влезе в селото и реши да...\r\n\r\nПоразпита местните жители, освен ако вече не го беше направил - иди на ###20###.\r\n\r\nСе насочи към къщата на прокълнатата девойка - иди на ###22###. Ако нямаш кодова дума „гривна“, можеш да отидеш до близкото езеро, освен ако вече не си бил там - иди на ###66###.\r\n\r\nАко нямаш кодова дума „вода“, можеш да посетиш местното гробище - иди на ###67###.'),
(37, 'Щом зърна старицата, четириногия му спътник се наежи, заръмжа страшно и след миг се впусна към нея по - бързо, отколкото Ги Су предполагаше, че е възможно. Той понечи да извика, но кучето с яростен лай се хвърли върху старата жена, която изкряка уплашено и падна на земята. Ги Су все още беше вцепенен от изненада и докато се надигаше иззад камъка, псето вече бе съдрало голямо парче от дрехата на старицата и се нахвърли отново върху нея.\r\n\r\nПреди да успее да я докопа, обаче тя замахна с ръка и кучето бе отхвърлено с нечовешка сила няколко метра назад. То падна тежко и нададе жалостиво квичене, заблиза ранения си крак, но в следващия миг превъзмогна болката си и отново с омраза се хвърли в битка. В същото време пред очите на Ги Су се разкриваше невероятна картина.\r\n\r\nСтарицата изправяйки се, започна цялата да се тресе, докато надаваше яростни къси кашлящи звуци. Образът и се замъгли и сви и тя се превърна в огромна бяла деветоопашата лисица. Хуаранг замръзна на място и целия настръхна от ужас. Та това същество беше кумихо! Хилядолетна зла, лисица чиято цел бе да отнеме жизнената енергия на достатъчно много хора, за да се превърне тя самата, завинаги в човек с полубожествена сила. Същество от най-страшните детски приказки, което олицетворяваше всичко зло. Митичен демон с изключителни магически способности, от които най - ярко изразена бе метаморфозата.\r\n\r\nДокато съзнанието му отказваше да приеме онова, което виждаха очите му, кучето се нахвърли върху съществото, което го пресрещна във въздуха и сключи острите си зъби около врата му. Четириногият му другар загина в същия миг, без да издаде звук и кумихо захвърли безжизненото му тяло на земята\r\n-	Кяф, кяф! - излая лисицата. - Мри, проклето псе!\r\n\r\nАко имаш умението „Самоконтрол“ и искаш да го използваш - иди на ###36###, ако не - на ###39###.'),
(38, 'Ако точките ти за познание са три или повече иди на ###70###. В противен случай продължи на ###98###.'),
(39, 'Очите на Ким Ги Су се напълниха със сълзи на мъка и ярост, заради зловещата кончина на четириногия му спътник. Бе го избавил от една смърт, за да попадне на друга, още по - ужасна. Това същество със сигурност беше в центъра на всичко случващо с в село Йо Су. Ето откъде произтичаха всичките слухове за проклятия и зли прокоби. Но не бяха слухове. Това трябваше да спре. Той някак трябваше да отърве селяните от злото същество.\r\n\r\nЗатова извади меча си, призова всичките си умения и реши да приключи с демона тук и сега, веднъж и завинаги. - иди на ###40###.\r\n\r\nВъпреки това реши засега да изчака, прикрит зад камъка и да наблюдава съществото. - иди на ###36###.'),
(40, 'Затова извади меча си, призова всичките си умения и реши да приключи с демона тук и сега, веднъж и завинаги\r\n-	Ти умри, изчадие! - изкрещя той, и посече кумихо. Тя отскочи назад с писък на болка и гняв, но пред очите на Ги Су раната се затвори за миг\r\n-	Знаех си, че и глупавият стопанин, на глупавото животно ще се навърта тъдява! - излая демона. - Време е и ти да умреш, младежо!\r\n\r\nГи Су се бори дълго и смело срещу нея, но докато нейните рани се затваряха пред очите му, неговите оставаха и бавно го омаломощаваха. Накрая тя проби защитата му и впи зъби в гърдите му, а след секунда докопа все още туптящото му сърце\r\n- Така загинал Ким Ги Су, смелия, но глупав хуаранг, който незнаел, че обикновенооръжиенеможе да нарани кумихо-завърши историятасикуангдеисе огледа триумфално,очаквайки дъжд от аплодисменти и монети. Вместо това, обаче към него полетя порой от яйца и развален зарзават.\r\n\r\nКрай.'),
(41, 'Той пое чашата като за миг докосна копринено нежната кожа на ръката й. Това докосване събуди смущаващо топло чувство някъде под диафрагмата му. За да прикрие смущението си, той я изпи на един дъх, докато Са Гуа го гледаше с красива, искряща усмивка.\r\n\r\nАко хуаранг притежава умението „Имунитет към отрови“ и искаш да го използва, или имаш кодова дума „отвара“ - иди на ###45###.\r\n\r\nВ противен случай - иди на ###44###.'),
(42, 'Ги Су потъваше в ласките на приказно красивата девойка и бавно чезнеше. Стана студено. Ставаше все по-трудно да диша. Ласкавата смърт беше близо. Скоро дойде тъмнината.\r\n\r\nИди на ###92###.'),
(43, '-	Кажи ми, кога започна всичко? - попита Ги С\r\n-	Баща ми твърди че е било след като мама е починала. Не помня много добре. Понякога ми си струва, че не помня нищо от живота си преди това - погледа й стана отдалечен и вглъбен\r\n-	Един ден сякаш се пробудих от дълъг сън. Скоро един младеж поиска ръката ми от татко, но горкичкият беше умрял на следващата сутрин. Оттогава започнах да усещам и лепкавите пръсти на проклятието. Аз съм прокълната Ги Су! Прокълната! Заради мен умряха толкова мъже! О, колко ме е страх! - девойката избухна в плач и се притисна в рамото му. Той започна да я потупва утешително по гърба. Беше толкова малка и нежна в ръцете му\r\n-	Не се тревожи Са Гуа. Ще намеря начин да ти помогна, обещавам\r\n-	Дано успееш, хуаранг. Дано успееш - тя го погали по мускула на ръката\r\n-	Ръцете ти са така силни и топли и в същото време нежни. Чувствам се на сигурно така - Ги Су се изчерви. Знаеше че трябва да я пусне, но не можеше, не искаше\r\n-	Няма страшно. Всичко ще се оправи\r\n-	Дали? - тя го погледна и в очите й видя отражение на чувствата й. Големите й черни, дълбоки очи го омайваха, притегляха. Той не можеше да откъсне погледа си от тях\r\n-	Дали? - повтори тя със стон, от който слабините му сякаш пламнаха.Тя леко го погали по бузата и за него това бе най - нежното докосване, което бе усещал някога.\r\n\r\nСа Гуа се отпусна на рамото му и започна да гали леко гърдите му, докато копринено нежния й дъх го галеше врата. Дишането на Ги Су се учести. Тя внимателно, но настойчиво го бутна на леглото и той не можа да намери сили да се противопостави. Нежните й ръце по тялото му го подлудяваха, а щом усети топлите й меки устни на врата си, от устата му се откъсна сподавен стон.\r\n\r\nАко имаш кодова дума „магия“ - иди на ###47###.\r\n\r\nАко нямаш тази кодова дума, но имаш умението „Самоконтрол“ и искаш да го използваш, или точките тиза Съзнание са повече от три - иди на ###48###.\r\n\r\nВ противен случай - иди на ###49###.'),
(44, 'Изведнъж се почувства много уморен и седна на леглото си - краката му отказваха да го държат\r\n-	Да не би да се умори, смели хуаранг? - попита тя доволно. Погледа на Ги Су се замъгляваше\r\n-	Ела красавецо, полегни си. Нека се погрижа за теб.\r\n\r\nТя с нежна настойчивост го бутна на леглото. Усети ръцете й да масажират тялото му\r\n- Колко е напрегнат, горкичкият\r\n-	Са Гуа! - изхриптя той. - Какво правиш! - Нещо определено не беше наред. Сякаш крайниците му бяха от олово, а клепачите му лепнеха един за друг\r\n-	Спри! Нед.. - в същия миг усети топлите й устни върху своите и потъна в дълбок сън.\r\n\r\nЗапиши кодова дума „сън“. Във виното имаше силен опиат, който замъглява и обърква възприятията.\r\n\r\nМини на ###72###.'),
(45, 'Виното беше добро, но имаше някакъв особен привкус. Ги Су забеляза, че тя не е докоснала своята чаша. Са Гуа продължаваше да го гледа сякаш очакваше да направи нещо и той седна на леглото\r\n-	Да не би да се умори, смели хуаранг\r\n-	Ами, малко. Беше дълъг ден. Но можем да поговорим, ако искаш\r\n-	Иди на ###43###.'),
(46, 'Ги Су внимателно отказа на девойката\r\n-	Съжалявам, Са Гуа, но не бих могъл. Искам съзнанието ми да е ясно тази вечер. Трябва да помисля\r\n-	За мен ли? - изкикоти се шеговито тя и остави на страна чашите. Ги Су се извъртя, за да не види тя почервенялото му лице\r\n-	Май те, обидих - каза девойката с искрена тъга\r\n-	Извинявай. Исках само да поговорим. Ще те оставя да си почиваш\r\n-	Не, не! - отвърна Ги Су\r\n-	Ти извинявай. Можем да поговорим, ако все още искаш. - той седна на леглото и девойката срамежливо, но грациозно се настани на прилично разстояние до него.\r\n\r\nИди на ###43###.'),
(47, 'Нещо обаче тревожеше съзнанието му, докато тялото му гореше от страст. С последните остатъци на волята си той изрече в скоропоговорка заклинанието, на което го беше научила мъдрата Че\r\n-	Шшшш - прошушна Са Гуа и впи устни в неговите.\r\n\r\nВ първия миг нищо не се случи и Ги Су беше готов да запуши гласа на разума си, за което нежното езиче на красавицата много помагаше.\r\n\r\nВ следващия момент, обаче, сякаш още един образ се появи през чертите й. Образ, който накара Ги Су да замръзне от ужас. Иззад красивите очи на Са Гуа го гледаха хитри, жълти животински зеници. През нежната й ръка го галеше рижава лапа с остри нокти, а езикът, който изследваше устата му, изведнъж, му се стори твърде дълъг, за да е човешки.\r\n\r\nИди на ###71###.'),
(48, 'С последните остатъци на самоконтрола си, той се възпротиви и внимателно, но твърдо я отблъсна от себе си\r\n-	Не бива, Са Гуа. Не това е начина, по който да ти помогна. Много си красива, но не е редно.\r\n\r\nДевойката смутено се изправи, изчерви се и прошепна смутено: -	Извини ме, хуаранг! Не знам какво ме прихвана! Моля те, не казвай на татко\r\n-	За нищо на света! - обеща Ги Су.\r\n\r\nСмутената красавица, още веднъж се извини, пожела му лека нощ, някак разочаровано и тихо излезе от стаята.\r\n\r\nГи Су дълго време не можа да накара ръцете си да спрат да треперят. В такива моменти, които не бяха никак много на брой му се искаше да е обикновен човек, а не хуаранг, длъжен да спазва правилата на рицарския орден. Отдаден на мисли изпълнени с разочарование той неусетно заспа.\r\n\r\nИди на ###50###.'),
(49, 'Нещо тревожеше съзнанието му докато тялото му гореше от страст, но въпреки това Ги Су беше готов да запуши гласа на разума си, за което нежното езиче на красавицата много помагаше.\r\n\r\nАко имаш кодова дума шаман, или точките ти за познание са ###3### или повече- иди на ###71###. Ако не иди на ###51###.'),
(50, 'Ким Ги Су сънуваше.\r\n\r\nИзбери ###82###, ###83### или ###84###.'),
(51, 'Ако имаш куче (и то е живо!) иди на ###90###, ако не иди на ###91###.');
INSERT INTO `episode` (`id`, `text`) VALUES
(52, 'Хората в Йо Су или бяха наистина наплашени, или не бяха особено гостоприемни. С доста питане Ким Ги Су в крайна сметка разбра къде е дома на кмета. Той се намираше в южния край на селото и беше една от най - големите и красиви къщи тук. Сградата бе типична за тази част от страната. Постройката беше под пречупена под прав ъгъл и бе опасана от висока човешки бой ограда. Покрива бе покрит с дялани дървени керемиди и красиви водостоци се спускаха към земята. Виждаше се че има просторна градина. Всичко във външния й вид говореше за къща на *янгбан, което бе странно за село с размерите на Йо Су. Хуаранг влезе през портата и веднага бе посрещнат от прислужник\r\n-	С какво мога да Ви помогна, благородни господине? - попита с хладна учтивост онзи\r\n-	Добър ден. Аз съм хуаранг Ким Ги Су и бих искал да говоря с кмета Канг Дже Хи по един важен държавен въпрос. Изпратен съм лично от един от магистратите и съветниците на краля\r\n-	Разбирам господине, ще предам на господин кмета Дже Хи за посещението Ви. Моля, последвайте ме.\r\n\r\nПрислужникът го отведе до един кокетен павилион в средата на китната градина, помоли го да изчака и изчезна вътре в къщата. След доста дълго чакане, прислужникът отново се появи, поклони се и обяви\r\n-	Кмет Канг Дже Хи желае да види вашите акредитиви. -	Моля?! - възкликна възмутен Ги Су\r\n-	Кмет Канг Дже Хи желае да види вашите акредитиви -повтори със същата учтива, незаинтересована интонация прислужника.\r\n\r\nПодобна проява на наглост дойде малко в повече на хуаранг. Единствено благодарение на отличният си самоконтрол, Ги Су успя да се сдържи да не реагира бурно на подобна обида. Той извади документа с каменно изражение и го подаде на слугата. Онзи го взе с трепереща ръка стреснат от спокойствието деминстрирано от хуаранг. След още едно десетминутно изпитание на търпението и себевладеенето му, слугата доситни до него и с леко треперлив глас обяви\r\n-	Кмет Канг ще ви приеме, хуаранг Ким Ги Су!\r\n\r\nГи Су не отвърна нищо. Със същото изражение на лицето се изправи и тръгна след припкащия отпред слуга, който го въведе в къщата. Стигнаха до една красива врата, на която прислужника боязливо почука\r\n-	Да? - чу се отвътре остър глас\r\n-	Хуаранг Ким Ги Су е тук господин кмете - обяви полушептейки слугата\r\n-	Нека влезе! - даде благоволението си гласът.\r\n\r\nГи Су бе въведен в приемната на кмета Канг Дже Хи, в която масата за писане бе единствената мебел\r\n-	Моля заповядайте! Седнете! - кметът изглеждаше странно познат отнякъде на хуаранг.\r\n\r\nГи Су предпочете да остане прав.\r\n\r\nКметът все още четеше документа, носен от Ги Су - заповед за съдействие на преносителя, състояща се от пет реда. Най - накрая той вдигна глава, пое въздух без никакъв опит да скрие отегчението си и попита\r\n-	С какво мога да ви бъда полезен, хуаранг? Ги Су отвърна с неприкрито раздразнение\r\n-	Ами, ако не ви затруднява твърде много, бих искал да ми разкажете какво се случва в село Йо Су - Ги Су добави със закъснение - господин кмете\r\n-	Нищо - отвърна онзи и след дълга пауза добави - Нищо, което да изисква вниманието на високоуважавания магистър, или на хуаранг с вашия статут. Нищо, с което да не можем да се справим\r\n-	Бихте ли бил малко по - подробен, господин кмете, относно това нищо, особено в частта, в която да не можете да се справите. Доколкото разбрах вчера е имало нов смъртен случай. С него стават шест за изминалата година. Може би, бих могъл да помогна да се справите с това „нищо“, за да можете да насочите усилията си към по-важни дела - като например тазгодишната чаена реколта. Магистърът се надява, че не бихте допуснали спад и тази година, ако случаят бъде разрешен по - бързо\r\n-	Откъде това любопитство в производството на зелен чай в Йо Су? - отвърна кметът. - Доколкото си спомням, ние не произвеждаме и един процент от общото производство в страната\r\n-	Откъде това любопитство, относно решения взети на високо ниво? - не остана длъжен хуаранг. - От кмета на едно село с размерите на Йо Су?\r\n\r\nКанг положи усилие и задържа напиращите думи зад езика си. -	Какво искате да знаете? - попита вместо това той\r\n-	Всичко, което знаете и вие - отвърна Ги Су\r\n-	Много добре. Хьо Уон Джин - бащата на девойката е съвестен жител и глава на едно от по - заможните семейства в поселището. Доколкото го познавах е здраво стъпил на земята, трудолюбив човек, макар с действията си напоследък да опровергава първото. Всичко започна, след като жена му почина преди близо две години. След смъртта на майка си Хьо Са Гуа стана затворено в себе си, ###14### годишно момиче, което почти цяла година не излизаше от вкъщи. Скоро след това тя бе променила коренно държанието си и започна усърдно, макар и достатъчно невинно да флиртува с младежите от село. Девойката наистина е много красива и няколко момчета се поболяха по нея. Отиваха да искат ръката й от баща й, а на следващата сутрин бяха откривани мъртви. Един се беше натровил със соджу, друг пак пиян се беше удавил в близкото езеро, а хищните риби там го бяха наръфали до неузнаваемост. Все подобни глупости, на днешната глупава младеж. Но хората тук са суеверни и започнаха да разнасят слухове за проклятия, за зли духове. Прости, селски хорица са тукашните - не можете да ги корите за страховете им. Но слухът плъзна извън селото, из областта, и така се стигна до невероятни истории. Няколко младежи от съседни села и градове също дойдоха да търсят ръката й. Сполетя ги същата злочеста съдба - не без намесата на други тукашни ухажори, но все още не съм открил доказателства, само подозрения. Накрая цялата история започна да се превръща в истерия до такава степен, че такъв здраво стъпил на земята човек като Хьо Уон Джин, прибегна до услугите на някаква незнайна шаманка. Домъкна от не знам къде и сега тая старица обикаля селото, плаши хората и всеки ден извършва ритуали със съмнителен характер и още по - съмнителен успех в къщата на Са Гуа -кметът си пое дъх\r\n-	Това е всичко което знам, уважаеми хуаранг и честно казано не одобрявам присъствието ви тук. Хората са достатъчно наплашени, а авторитетът ми подкопан, за да ги настървявате допълнително с присъствието си. Нямам достатъчно хора да потушавам бунтове или линчове. Така че ви желая успех с мисията ви, защото в противен случай, ще бъда принуден да викам кралската гвардия тъдява, за да успокоява хорските духове\r\n-	Разбирам притесненията ви, уважаеми кмете, но не се тревожете - аз ще стигна до дъното на историята, а присъствието ми ще успокои хората. Благодаря ви за отделеното време. Няма да ви притеснявам повече. - Ги Су се накани да си тръгва\r\n-	Как е майка ви, хуаранг Ги Су? Ким Ги Су замръзна\r\n-	Почина преди няколко години. Защо се интересувате? -	Познавах я много отдавна, когато служех в столицата.\r\n\r\nСъжалявам да науча тези скръбни новини. - отвърна безизразно кметът\r\n-	Благодаря. - отвърна хуаранг с ледена сдържаност.\r\n\r\nЧак сега се сети откъде му бе познат кметът Канг. Преди години кметът Канг бе магистър Канг, който, под влияние на кралицата, бе повдигнал несправедливо обвинение към майка му. В последствие, той бе заточен на дребна чиновническа служба някъде из страната. Очевидно - като кмет на село Йо Су\r\n-	Радвам се да видя, че сте забравил враждата, която имахте с нея. Сега знам, че спокойно може да почива. Виждам, че сте се разкаял за стореното, след като с такава готовност ме приехте и толкова подробно ме осведомихте за случващото се тук - всяка дума на Ги Су сякаш бе удар с бич. - Можете да сте сигурен, че в столицата ще научат за това\r\n-	Благодаря ви, хуаранг - изрече с неприкрита омраза и леко притеснение кметът. - Има ли нещо друго, което мога да направя за вас? - неохотно добави той.\r\n\r\nАко имаш умението Самоконтрол и искаш да го използваш - иди на ###16###.\r\n\r\nВ противен случай иди на ###20### и потърси друг вариант да събереш информация.\r\n\r\nМожещ също да огледаш околностите - иди на ###21###.\r\n\r\nИли да се насочиш към дома на прокълнатата девойка - иди на ###22###.'),
(53, 'Ким Ги Су сравнително бързо намери местната кръчма и влезе вътре. Помещението беше тъмно, влажно, не особено чисто и се състоеше от няколко ниски масички и тезгях. Въздухът беше спарен и напоен с миризма на мухъл, алкохол и готвено.\r\n\r\nОколо една от масите имаше група от няколко младежа, насядали по пода, които изгледаха подозрително хуаранг.\r\n\r\nЕдинственият друг посетител се бе усамотил на една отдалечена маса и съсредоточено изливаше чашка след чашка алкохол в устата си с отсечени движения, като след всеки гълток лицето му се изкривяваше сякаш пиеше по принуда.\r\n\r\nСобственикът на кръчмата бе застанал зад тезгяха и наблюдаваше Ги Су с хладно любопитство, примесено със страх и подозрение.\r\n\r\nГи Су се позамисли с кого да се опита да поговори. Със собственика - иди на ###59###\r\n\r\nС групата младежи - иди на ###60###.\r\n\r\nСъс самотния тип, отдал се на компанията на чашка соджу в ъгъла - иди на\r\n\r\n61.'),
(54, 'Ким Ги Су реши да потърси местната шаманка. Тя се казвала мъдрата Че, както разбра от един не чак толкова свъсен и неуслужлив селянин. Къщата й се намирала почти в края на селото. Нямало как да я сбърка. Носа му щял да води краката.\r\n\r\nКогато наближи колибата, установи, че селянина не го беше излъгал - разнасящата се миризма, нямаше как да го подведе. Тя бе образувана от такова разнообразие, че беше трудно да се определи преобладаващ мирис, но беше безпогрешен ориентир, дори човек да страдаше от тежка хрема. Самата колиба бе в добро състояние, но си беше колиба, а неразборията около нея беше невероятна. Най -различни сечива и приспособления, за какво ли не, образуваха нещо като странна ограда. До колибата се издигаше шаманското дърво, окичено с всякакви парцалчета и тотеми - източник на сила на шамана. Учудващо бе, че зад колибата имаше неголяма, но отлично поддържана зеленчукова градина и няколко плодни дръвчета. Домашни животни естествено нямаше - никой шаман не консумира месо, мляко или яйца.\r\n\r\nГи Су се приближи и похлопа на вратата. Ако имаш кодова дума „шаман“ - иди на ###55###. Ако не - иди на ###56###.'),
(55, 'На третото настойчиво похлопване, Ги Су реши, че мъдрата Че или не си е вкъщи, или е съвсем глуха. След кратко колебание натисна дръжката на вратата, която се оказа отворена.\r\n\r\nТой влезе вътре и замръзна при вида на гледката, която се разкри пред очите му. Старата шаманка лежеше в средата на колибата, с крайници замръзнали в неестествени пози и изцъклени очи. Под нея се бе образувала огромна локва кръв. Толкова много кръв. В гърдите на старицата зееше дупка с размера на юмрук. Ги Су се приближи и усети как косата му настръхва - сърцето й беше изтръгнато. Той се досещаше какво е причинило смъртта й. И щеше да се постарае скоро да си плати. Прошепна молитва към духовете да приемат изтерзаната душа на мъдрата Че и даде мълчаливо обещание, че смъртта й ще бъде отмъстена. После взе няколко главни от огнището, на което къкреше някаква течност и ги разхвърля в ъглите на колибата.\r\n\r\nПречистващата сила на свещения огън, щеше да бъде достойно погребение за старицата. Излезе тихо и се увери, че никой не е забелязал присъствието му тук, след което с мрачна решителност реши да...\r\n\r\nсе отправи към местната кръчма, където да потърси информация за случващото се, освен ако вече не беше ходил там - иди на ###53###.\r\n\r\nсе отправи към кметството, където да поговори с представителя на официалната власт, ако вече не беше го направил - иди на ###52###.\r\n\r\nда се запъти към къщата на прокълнатата девойка, за да се опита да приключи с всичко това веднъж и завинаги - иди на ###22###.\r\n\r\nили да проучи покрайнините на селото, освен ако вече не го беше правил - иди на ###21###.'),
(56, 'На третото настойчиво похлопване Ги Су реши, че мъдрата Че или не си е вкъщи, или е съвсем глуха. След кратко колебание натисна дръжката на вратата, която се оказа отворена. Влезе в колибата и пред очите му се разкри страховита гледка. Мъдрата Че, очевидно едра жена, се бе навела над някакво къкрещо гърне, излагайки на показ цялата прелест на задните си части, които заемаха сериозна част от пространството в колибата\r\n-	Още колко време ще ти е нужно, за да се сетиш да затвориш вратата? - чу се плътен женски глас, който извади Ги Су от вцепенението и той бързо затвори вратата. Старицата все така си беше наведена над гърнето и той се зачуди къде да дене погледа си, за да избегне не особено приятната, но натрапваща се гледка\r\n-	Сядай някъде де! Цял ден те чакам! Хуаранг послушно седна на земята\r\n-	Младежо, майка ти не те ли е научила, че не е възпитано да се вторачваш така в хората? - сопна се старицата, изправяйки се изведнъж. Стреснат Ги Су за малко да се катурне по гръб. Въпреки че беше едра и доста възрастна, жената пред него все още носеше следите на отдавна отминала красота. Но едно нещо наистина го удиви - млечнобялото перде върху зениците на старицата\r\n-	Извинете, уважаема госпожо\r\n-	Няма нужда, скъпо момче! Майка ти няма как да ти е обяснила, че на нас жените това не винаги ни е неприятно - изкиска се старата шаманка. - Радвам се че и на тези години пълня окото\r\n-	О, да, госпожо! Наистина е трудно да останете незабелязана! -опита се да прекрати неловкия за него разговор Ги Су\r\n-	Аигоо! И какъв е галантен. Наистина си сладък като се червиш така\r\n-	Ама вие как разбрахте..\r\n-	Че се червиш, ли? Видях те, момчето ми! Макар и не с очите си. Все пак съм шаманка, забрави ли\r\n-	Не, не! Съвсем не! Просто... - хуаранг се покашля от неудобство. -	Да не си настинал? Искаш ли един чай\r\n-	Не, не! Вижте уважаема госпожо Че..\r\n-	Откъде знаеш че съм госпожа? - сопна се шаманката. - За втори път ме обиждаш! Бива ли да се отнасяш така с хората, от които очакваш помощ\r\n-	Извинете, госпожице Че\r\n-	Така е по-добре! Ама най -добре ще е просто Че. Няма нужда от чак такива официалности\r\n-	Вижте, госп... мъдра Че, тук съм заради..\r\n-	Както вече ти казах, скъпо момче, знам за какво си тук! Идваш да питаш за Хьо Са Гуа - прокълнатата девойка. И какво да направиш, за да вдигнеш проклятието, нали\r\n-	Точно така, уважаема Че\r\n-	Така! А сега ме слушай без да ме прекъсваш. Хьо Уон Джин - бащата на Са Гуа, сам покани злото в дома си. Всичко започна преди две години, когато домъкна някаква млада жена, която бил спасил от смърт по време на едно от пътуванията си и я направи слугиня в къщата си. Жена му безкрайно го обичаше, но странно защо започна да го ревнува от слугинята. Докато един ден, както се говори, не го хванала наистина да й изневерява с нея. Това подлуди жена му, която за малко да пребие до смърт прислужницата. На следващата нощ слугинята просто изчезна. Някои слухове твърдят, че жена му наистина убила слугинята през нощта, а после се отървала от тялото, но аз не вярвам. Щях да усетя. Месец след това съпругата на Хьо Уон Джин се поболя и скоро почина. Стопи се буквално пред очите ми без да мога да направя нищо, с което да й помогна, или поне да облекча мъките й. Хьо беше покрусен, но ужасът за него не свърши с това. След смъртта на майка си, Са Гуа - дъщеря му изпадна във вцепенение от мъка. Спря да се храни, да спи, да говори, спря да живее. Просто съществуваше. Сякаш душата й си бе отишла, заедно с майка й и нищо не можеше да я върне към живота. Как ли не се опитвах да помогна на бедното дете, но с нищо не успях. А с месеци се борих за нея. Нещо изсмукваше живота от нея бавно, но сигурно. Докато един ден, най - неочаквано тя се оправи. Започна да се храни, да се смее плахо, да говори. Но това вече не беше Са Гуа - беше нещо друго. Нещо ужасно -старицата потрепера при спомена. - Опитах се да обясня на баща й, но той не искаше и да чуе - неговата Са Гуа се беше върнала и нищо друго нямало значение. Тогава ме изгони и каза, че не желае да ме вижда дори да доближавам до дъщеря му. А нещото в Са Гуа бавно започна да променя тялото й по свой вкус. Тя пстепенно се превърна в най-красивата девойка не само в селото и околността, а може би и в цялото кралство. Започна да омайва младежите от село, но не с невинността на неопитна девойка, а с майсторството на дългогодишна кисенг. Момците пощуряха. Започнаха побоища, разпри. Скоро един от тях поиска ръката й. На другата сутрин го намериха мъртъв, с почернял език, сякаш отровен с бързодействаща отрова. Никога няма да забравя погледа в изцъклените му очи - изпълнен с блаженство - сякаш наместо смъртта си бе изпитал върховно удоволствие. Кметът обяви, че се бил отровил от алкохол. Повечето повярваха, но не и мъдрата Че. Нещо беше убило младежа - беше изсмукало същността му и го беше накарало да вярва, че това е най-прекрасното нещо на света. Следващият младеж бе открит разкъсан в близкото езеро. Рибите го били наръфали така - ха! - изсмя се горчиво шаманката. - Не бях виждала риба да изяде черния дроб на човек и да остави всичко друго непокътнато. Друго беше убило момъка - същото като първия. Тогава стария Хьо домъкна отнякъде, някакво същество, което се представя за шаманка - старата Гун. Тя не е човек, момко! Стой настрана от нея! Тя е зло! Зло в човешка форма! А нещото, обладало Са Гуа все още не е толкова силно. Все още набира сили - с него може би ще можеш да се справиш. Какво е - не знам. Страх ме е дори да предполагам. Мога само да се надявам силен момък като теб да може да се справи с него и ако може да освободи бедното дете.\r\n\r\nГи Су остана безмълвен, но космите на тила му бяха настръхнали\r\n-	Не мога да ти помогна с много, синко, но все пак мога...\r\n\r\nАко хуаранг владее уменията „Шаманизъм“, „Харизма“ поне на ниво майстор, или точките му за познание са три, или повече - иди на ###57###.\r\n\r\nВ противен случай - иди на ###58###.'),
(57, '-	... да те науча на едно простичко заклинание, понеже усещам, че имаш знанията и умението да го приложиш. С него ще можеш да видиш реалността - такава каквато е, а не такава каквато я възприемат сетивата ти.\r\n\r\nГи Су прекара следващия час в заучаване на безмислените, поне за него, няколко думи, от които се състоеше заклинанието, и когато шаманката остана задоволена от резултата го остави на мира. Част от упражненията включваха изговаряне думите на обратно, в случаен ред и с пълна с вода уста. Накрая Ги Су смяташе, че би могъл да изрече безсмислицата и насън.\r\n\r\nНа сбогуване старицата му пожела успех и изрече просто заклинание, което възвърна част от ки-енергията му.\r\n\r\nЗапиши кодова дума „магия“, прибави една точка Съзнание, Познание и ки-енергия. Всеки път, когато имаш възможност да използваш заклинанието, това ще бъде изрично упоменато. Употребата му изисква разхода на една точка ки-енергия, която ще трябва да извадиш от статуса си без това да е указано в текста.\r\n\r\nСлед като излезе от колибата Ги Су се замисли какво да предприеме по - нататък. Можеше да:\r\n\r\nсе насочи към местната кръчма, за да потърси информация от местните посетители, освен ако вече не го беше правил - иди на ###53###.\r\n\r\nпотърси кмета на селото и да го разпита за случващото се, ако вече не го беше направил - иди на ###52###.\r\n\r\nда се насочи право към къщата на набедената за прокълната девойка - иди на ###22###.\r\n\r\nда разгледа покрайнините на селото - иди на ###21###.'),
(58, '... да ти дам ето тази отвара. Да знаеш, че е отвратителна на вкус, но пък е мощна противоотрова и пречистващо организма средство. Обезсилва отровата веднага, а очистителният ефект започва на другия ден. Препоръчвам ти да не плануваш пътувания, или излизания от дома в този конкретен ден - ще си провалиш плановете.\r\n\r\nСтарицата му връчи малка колбичка с ужасно изглеждаща течност.\r\n\r\nНа сбогуване тя му пожела успех и изрече просто заклинание, което възвърна част от ки-енергията му.\r\n\r\nЗапиши кодова дума „отвара“, прибави една точка Познание и ки-енергия.\r\n\r\nСлед като излезе от колибата Ги Су се замисли какво да предприеме по - нататък. Можеше да:\r\n\r\nсе насочи към местната кръчма, за да потърси информация от местните посетители, освен ако вече не го беше правил - иди на ###53###.\r\n\r\nпотърси кмета на селото и да го разпита за случващото се, ако вече не го беше направил - иди на ###52###.\r\n\r\nда се насочи право към къщата на набедената за прокълната девойка - иди на ###22###.\r\n\r\nда разгледа покрайнините на селото - иди на ###21###.'),
(59, 'Ги Су реши да се опита да поговори със собственика, който като видя запътилия се към тезгяха непознат се начумери още повече. -	В мир ли сте? - поздрави хуаранг\r\n-	В мир, в мир. А ти мирен ли си? - отвърна троснато онзи. -	Хвала на Буда, засега да\r\n-	Какво ще обичаш\r\n-	Ами чашка соджу - отвърна Ким Ги Су. - Какво има за хапване? Мирише чудесно - излъга той\r\n-	*Кимпап с пушена риба. Искаш ли? -	Да, моля. Прегладнял съм\r\n-	Чудесно. Четири кралски вона - плаща се предварително - обяви кръчмарят.\r\n\r\nГи Су порови в кесията си и извади една монета, равняваща се на пет кралски вона, остави я на тезгяха и каза\r\n-	Задръжте рестото добри ми, кръчмарю!\r\n\r\nПри вида на монетата и проявената щедрост, лицето на онзи просветна. Със сигурност обявената цена беше в пъти по-висока от реалната стойност, а оставеният бакшиш беше кралски. Изведнъж кръчмарят коренно промени отношението си\r\n-	Благодаря ви, достопочтени господине! Сигурно идвате отдалече и сте уморен от пътя. Eлате, заповядайте да седнете тук.\r\n\r\nКръчмарят го отведе до една уединена и чиста маса\r\n- Починете си, а аз ей сега ще ви донеса храната и питието - каза онзи и се отдалечи с поклон.\r\n\r\nСлед по-малко от две минути на масата имаше чаша пълна със соджу и чиния с добре изглеждащи, ароматни кръгчета кимпап\r\n-	Заповядайте господине и да ви е сладко! Добре се нахранете! Ако имате нужда от още нещо.\r\n-	Всъщност, да - прекъсна го хуаранг. - Бихте ли ми правили компания, докато се храня? Тъкмо ще можем да побъбрим. Отдавна не съм имал възможността да поговоря с достоен събеседник\r\n-	Ами... - кръчмарят се поколеба за миг\r\n-	Хайде, моля ви! - настоя Ги Су. - Я направо донесете и за себе си една чашка. - онзи се ухили в отговор\r\n-	Е, добре господине. Ще се радвам да ви правя компания. След минутка кръчмарят се върна и седна неуверено срещу хуаранг. Ги Су напъха едно парче кимпап в устата си и замляска шумно, в знак на уважение към добрата храна\r\n-	Ммм. Много е вкусно. Чудесен кимпап\r\n-	Яжте до насита, господине! - засия онзи\r\n-	Я, ми кажете, добри ми кръчмарю, всичко наред ли е с тазгодишната реколта на зелен чай\r\n-	Да, господине! Плодородна е годината! Тук в Йо Су се произвежда най-добрият зелен чай в цяла Шилла! Вие да не би да сте търговец\r\n-	Аха! А кмета гледа ли си съвестно задълженията\r\n-	Да, господине! Мъдрият ни кмет Канг Дже Хи е стриктен, съвестен и справедлив управник. Доволни сме от него. Да не би да сте инспектор\r\n-	Така, така! - Ги Су се оригна шумно. - А живата стока? Здрава ли е? Множи ли се\r\n-	Здрави са животните, господине! Роят се и имаме от всичко колкото ни трябва, даже остава и за търговийка. Вие сигурно сте данъчен инспектор! - кръчмарят бе започнал да става подозрителен\r\n-	Не, не! Слава на Буда, не съм! - онзи видимо се отпусна. -	Тогава защо задавате все такива въпроси\r\n-	Ами защото хората в селото ми се сториха едни такива, угрижени, начумерени, умислени. Дори изплашени. На пристигане една старица, след като я поздравих, ми отвърна че мира си е отишъл от село Йо Су, та се замислих, защо така? Мислех че може би е заради слаба реколта, или недостатъчно съвестен управник, или проблеми с добитъка, но разбирам, че не е така и съм още по-озадачен.\r\n\r\nКръчмарят остана мълчалив и умислен за момент\r\n-	Ех, господине, какво да ви кажа! - въздъхна тежко той. - Други са нашите проблеми, различни са грижите ни. Нима не сте чувал за проклятието, което тегне над най-красивата девойка в цялата област - нашата красива Са Гуа\r\n-	Не, не съм! - отвърна Ги Су. - Кажете, честни кръчмарю, за какво проклятие говорите\r\n-	Всички злини в селото ни идат от нейното проклятие. Тя е най - красивата девойка не само в селото, но и в околността, а мен ако питате и в цялото кралство. Колкото е красива, обаче, толкова е злочеста и съдбата й. Момичето произлиза от добро семейство, но с лоша карма. Баща й е един от най-големите производители и търговци на зелен чай в околията, а майка й беше умна, красива и смирена. Да, майка й почина преди около две години. Оттогава започнаха и злочестините на село Йо Су. Са Гуа се поболя от мъка и цяла година не излиза от дома си. След това, обаче изведнъж се завърна в света на живите. Само дето нямаше и помен от срамежливото девойче. Беше се променила коренно и започна да предизвиква съвсем целенасочено, макар и с престорена невинност младежите от село. Няколко се поболяха по нея. Отидоха да й искат ръката от баща й, а на следващата сутрин бяха откривани мъртви. Един се беше натровил със соджу, друг пак пиян се беше удавил в близкото езеро, а хищните риби там го бяха наръфали до неузнаваемост. Все подобни глупости на днешната глупава младеж. Даже за малко да обвинят мен, че съм ги отровил, представете си! Очевидно беше че девойката е прокълната и тукашните започнаха с право да се страхуват за съдбините си. Вместо като предупреждение, мълвите подействаха като кимчхи в горещ ден на младежите от околията. Няколко дойдоха да искат ръката й, но за зла беда пак погиваха току на другия ден. Един го разкъсали вълци, друг се хвърлил от скала, третия... Накрая Хьо Уон Джин изнамери някаква незнайна шаманка и сега тая старица обикаля селото, плаши хората и всеки ден извършва ритуали в къщата на Са Гуа - кръчмарят си пое дъх. - Та това е мъката ни господине\r\n-	Разбирам - отвърна хуаранг.\r\n\r\nДокато двамата с кръчмаря говореха, Ги Су не бе забелязал как групата младежи си бе тръгнала\r\n-	Е, господине, побъбрихме си добре - каза съдържателят и се надигна\r\n-	Добри кръчмарю, само за момент още! - спря го хуаранг. Онзи се върна на мястото си и го погледна с очакване - Вие сте наистина интелигентен и разумен човек, господин кръчмарю и макар да ви познавам отскоро, нещо ми подсказва, че мога да ви имам доверие.\r\n\r\nГи Су си придаде най-искреното изражение, на което бе способен. -	Нека ви попитам нещо. Можете ли да пазите тайна?\r\n\r\nОчите на кръчмаря блеснаха от любопитство и той нетърпеливо кимна\r\n-	Разбира се, господине\r\n-	Знаех си, че мога да разчитам на вас. Вижте сега - той се наведе към кръчмаря, който инстинктивно също се наведе към хуаранг съзаклятнически. - не бива да казвате на никого това което ще ви споделя, под заплаха от държавна измяна - онзи ахна и се опита да се отдръпне, но Ги Су го сграбчи за ръката, с която се бе подпрял на масата, приковавайки го на място\r\n-	Всъщност аз съм тук на тайна държавна мисия. Задачата ми е да разбера кой стой зад поредицата смъртни случаи, или убийства и да сложа край на това. Виждам, че вие сте наблюдателен човек, който за малко сам да пострада от случващото се. Със сигурност сте забелязали още нещо, което да е събудило правдиво подозрение у вас, или сте стигнал до заключение, благодарение на вашата вродена досетливост и остра логика. Ако споделите това с мен ще ви бъда много благодарен. Държавата ще ви бъде много благодарна - лично ще се погрижа за това, както и за дискретността на изказаната благодарност. Ги Су многозначително поразтърси кесията си. Ако хуаранг владее умението Харизма и искаш да го използваш- иди на ###16###.\r\n\r\nВ противен случай кръчмарят отказва категорично да разговаря повече и ти можеш да:\r\n\r\nпотърсиш друг начин да събереш информация - иди на ###20###. да огледаш покрайнините на селото - иди на ###21###.\r\n\r\nда се насочиш към дома на прокълнатата девойка - иди на ###22###.');
INSERT INTO `episode` (`id`, `text`) VALUES
(60, 'Ким Ги Су реши да се насочи към групата младежи, наобиколили една от масите в центъра на кръчмата\r\n-	В мир ли сте? - учтиво поздрави той\r\n-	Да - отвърна му един от тях, докато останалите го оглеждаха със смесица от любопитство, подозрение и враждебност\r\n-	Имате ли нещо против да поседна при вас, приятели, пътувах дълго и съм зажаднял за свежа компания, с която да споделя храната и питието си - учтиво попита Ги Су\r\n-	Ами щом си решил да споделяш хапване и пийване, заповядай! -отвърна друг, а останалите видимо се отпуснаха\r\n-	Хей кръчмарю, ела насам, дошъл ти е гладен и уморен посетител, а нас ни мъчи жажда... за приказки! - младежите избухнаха във весел смях и той се присъедини, развеселен от незлобливата шега.\r\n\r\nГи Су поръча по чашка соджу на всички и три порции кимпап, разплати се със съдържателя, като му остави кралски бакшиш, въпреки завишените цени и се намести удобно сред младежите\r\n-	Кажи сега, друже, какво те води в нашето китно село\r\n-	О, служебна работа, просто минавам оттук - нищо интересно. А, я вие момци, ми кажете, защо всички в селото са така начумерени и угрижени\r\n-	Ами на такива си попаднал! Я ни виж нас\r\n-	Дам! Весели момци сте вие! Ама чувам някакви истории за проклятия, за девойки - такива измислици едни.\r\n\r\nИзведнъж всички младежи замлъкнаха и изгледаха враждебно Ги Су\r\n-	Или не са измислици? - допълни той\r\n-	Кой всъщност си ти страннико? - попита един. -	И каква е работата ти тук? - додаде втори\r\n-	И ти ли ще искаш ръката на Са Гуа? -	Омръзна ни от такива като теб\r\n-	Да\r\n-	Я да си събираш нещата и прав ти път!\r\n\r\nМладежите започнаха да се изправят от местата си\r\n-	Я се успокойте! - кресна им Ги Су. - Са Гуа - ако така се казва тази ваша прокълната девойка - да, заради нея съм дошъл, но не да искам ръката й\r\n-	А защо тогава\r\n-	За да разбера какво се крие зад тази история и да сложа край\r\n-	Ха! - изсмя се един от младежите. Ти да не се мислиш за хуаранг У Гуан Хан, страннико\r\n-	Всъщност, като го спомена, Гуан Хан и аз сме добри приятели -онзи зяпна\r\n-	Да бе, сигурно ще да си Ким Ги Су, тогава, а? -	Я! - възкликна хуаранг. - Вие ме познавате!\r\n\r\nМладежите избухнаха в смях\r\n-	Хайде, стига се занасял, вече! Добър шегаджия си, но ни кажи кой си и каква работа имаш в село Йо Су?\r\n\r\nГи Су бавно отвърза прибрания в ножницата си меч, взе го в две ръце и го обърна към младежите\r\n-	Някой от вас може ли да чете? - попита той. -	Аз - отвърна един. - Малко\r\n-	Юнак! - похвали го хуаранг. - Можеш ли да прочетеш какво пише ето тук - на ефеса на меча?\r\n\r\nМеча на един рицар хуаранг бе произведение на ковашкото изкуство, създаден специално за него, съобразен с неговото тегло и ръст, силни и слаби страни в боя. Всеки такъв меч носеше името на притежателя си върху ефеса\r\n-	Амми. К..И..М - младежа се затрудняваше неимоверно. - Г...Н... не, не ...Г...И... - лицата на останалите момци започнаха да се изопват. - С..., а това е, ъъъ.да! У! Ким Ги Су! - възкликна радостен онзи и се огледа победоносно, но след миг осъзна какво бе прочел и пребледня. Изведнъж младежите вкупом паднаха на колене и започнаха да удрят чела в пода, кланяйки се и да хленчат един през друг\r\n-	О, благородни хуаранг, прости на нас неуките и глупави селяци! -	Пощади ни, господине\r\n-	Милост, господарю\r\n-	Я, ставайте веднага! - сопна се Ги Су. - Успокойте се! Нищо няма да ви сторя, ако си мълчите за присъствието ми тук. - той се огледа, но с облекчение установи, че кръчмарят се е изнизал в стаичката си зад тезгяха, а самотният клиент кротко спи с лице върху масата\r\n-	Я сега седнете обратно, пийнете и ми разкажете какво знаете за случващото се в село Йо Су\r\n-	Старите твърдят, че за нещастията в селото е виновна Са Гуа, но не е така да знаете. Баща й е виновен за проклятието\r\n-	Не! - намеси се друг младеж. - Не е виновен господин Хьо Уон Джин! Виновна е онази вещица - старата Гун\r\n-	Не е вярно! - прекъсна го трети. - Сама си е виновна за проклятието\r\n-	Чакайте, малко! - озапти ги Ги Су. - Един по един и не се прекъсвайте! За какво да е виновен баща й\r\n-	Ами вижте сега, той господин Уон Джин е важна клечка в селото - богаташ. Върви му търговията със зеления чай. Ама откакто жена му почина преди две години започна всичко. Са Гуа беше още малка тогава и се поболя. Баща й вместо да я разведрява като води при нея дружките й я затвори в къщи за цяла година, като си мислеше, че ще прогони демона на болестта. И изведнъж, точно една година след като почина майка й, Са Гуа се оправи като по чудо. Убеди баща си да я пусне да излиза навън. Каква красавица беше станала само малката Са Гуа! Като я види човек и забравя за къде е тръгнал и какво ще прави. А пък като ти се усмихне и не можеш да мръднеш! Само седиш и се хилиш, докато не се намери някой да ти зашлеви един и да те изтръгне от мечтите! Та мина се месец - два и се намери първия смелчага да й поиска ръката от баща й. Никой не знае какво се е случило, но горкия се беше напил до смърт и го намериха изстинал на другата сутрин. След още месец втори смелчага също ходи при баща й. Него го намериха удавен в езерото след няколко дни и рибите добре го бяха оръфали. Тогава стана ясно, че след като не я дава за жена баща й е разлютил демона на болестта й и сега злочестата Са Гуа страда заради това\r\n-	А ти защо смяташ че Са Гуа е виновна\r\n-	Ами как защо? То всичко си е както го каза Пу Мо, само дето след като се оправи Са Гуа, освен че се беше разхубавила, промени и държанието си. Започна да кокетничи, да се задява уж невинно с момците, сама предизвика отново демона на болестта си. Порядъчна девойка не се държи така\r\n-	Говориш така, защото ти се присмя на сплескания нос, Янг Гу! - намеси се третия младеж\r\n-	Носа ми не е сплескан, той е изразителен! Другите младежи избухнаха в смях\r\n-	 Стига, стига! - опита се да бъде сериозен Ги Су. А ти я ми кажи коя е тая Гун\r\n-	Старата Гун? Старата Гун е някаква шаманка, която Хьо Уон Джин домъкна от немай къде, след като още двама юнаци от околията се споминаха току, след като бяха искали за жена Са Гуа. Зловеща жена е тази, да знаете - и кучетата бягат от нея. Точно тая вещица със заклинанията си е проклела Са Гуа. Всеки ден е в къщата й. Всеки ден засилва проклятието си, така ще знаете! - младежът си пое дъх. - Та това е накратко господин рицарю\r\n-	Разбирам - отвърна хуаранг.\r\n\r\nМладежите се опитаха да си намерят занимание, отпивайки от чашите, или дъвчейки парче кимпап в неловко последвалата тишина\r\n-	Благодаря ви за информацията, момчета! - наруши мълчанието Ги Су.- Струва ми се обаче, че будни момци като вас, трябва да са забелязали още нещичко в тази странна история.\r\n\r\nМладежите се спогледаха сконфузно и един от тях смутолеви: -	Ами, може и да сме, но..\r\n-	Но? - настоя Ги Су\r\n-	Ще ви кажем, но само ако ни покажете някой интересен удар с меч\r\n-	Или ритник! - додаде друг. -	Или блок\r\n-	Или...\r\n\r\nКим Ги Су въздъхна вътрешно. Явно щеше да му се наложи да се прави на палячо, за да получи още някаква информация.\r\n\r\nАко Ким Ги Су притежава умението майстор на меча или майстор стрелец и искаш да го използваш- иди на ###16###.\r\n\r\nАко няма тези умения, но искаш да получиш допълнителната информация от младежите, това ще ти струва ###3### точки ки-енергия. Ако смяташ че си заслужава да ги пожертваш - иди на ###16###.\r\n\r\nАко няма горните умения и не желае да хаби ценната си ки-енергия, той любезно отхвърля молбите на младежите и напуска кръчмата - иди на ###20### ако искаш да търсиш друг начин да получиш информация, или на ###21### ако искаш да огледаш покрайнините на селото, или на ###22###, ако искаш да се насочиш към къщата на прокълнатата девойка.'),
(61, 'Ким Ги Су се насочи към самотния посетител на отдалечената маса и седна при него\r\n-	Имаш ли нещо против да седна при теб? - попита той. Онзи дори не погледна към него, а отвърна с неопределено сумтене, което Ги Су предпочете да приеме като покана. Извика кръчмаря и поръча две порции кимпап - една за себе си и една за бъдещия си събеседник и бутилка соджу. Онзи изсумтя доволно при вида на храната и алкохола и се зае да ги опустошава\r\n-	Много са странни хората тъдява - подхвърли Ги Су. - Едни намусени, подозрителни. Защо така, а приятелю?\r\n\r\nПолучи поредното неопределено сумтене. Наля нова чашка на непознатия, който така и не поглеждаше към него\r\n-	Странни слухове се носят за вашето село, знаеш ли? За проклятия разни. Сигурно са измислица, знам ли? Ти чул ли си нещо? - онзи упорито отбягваше да го погледне. Хуаранг започваше да губи търпението си. Да не би да си говореше със слабоумен\r\n-	Някаква прокоба тегнела над тукашна девойка, чувам. Всички момци дето поискали ръката й, умирали на другата сутрин. Не може да не си чувал за това. - онзи за пръв път изгледа Ги Су с ужас в очите и отривисто поклати глава в знак на отрицание. Понечи да стане и да си тръгне. Ги Су набързо огледа помещението - кръчмарят се бе скрил в стаичката зад тезгяха, а групата младежи бе излязла от кръчмата. Той хвана необщителния си събеседник и го дръпна обратно на земята, сграбчи го за яката и процеди през зъби\r\n-	Виж сега, нахраних те и те почерпих кралски. В замяна искам просто да ми кажеш какво знаеш - човекът продължи да се блещи насреща му и да върти глава\r\n-	Хайде, човече! Успокой се и ми кажи какво знаеш! - Ги Су повиши тон и го разтърси леко\r\n-	Кх. Кхуи... шин! - изфъфли той и хуаранг видя, че човека е с отрязан език. Съжали веднага за поведението си и каза\r\n-	Съжалявам, приятелю! Не знаех за недъга ти! Моля да ме извиниш! - бръкна в кесията си и извади десетина монети. - Ето, с това ще можеш да направиш малко по- лесен живота си. И недей да ги пропиваш! - очите на човека се напълниха със сълзи на благодарност, пое монетите с треперещи ръце, стана и се отправи към вратата, кланяйки се на Ким Ги Су на всяка крачка. Самият той за малко да се просълзи\r\n-	Кхуи...шин! - изломоти онзи пак, поклони се за последно и излезе.\r\n\r\nГи Су изпи чашата с алкохола си на един дъх и се опита да се вземе в ръце, въпреки че все още му беше гузно. Куишин бе казал онзи. Зъл дух. Нима беше вярно? Не - по-скоро човека бе наплашен истински. Това бе същество, с което бабите плашеха непослушните деца, излязло от приказките и легендите. Нищо повече. Горкият човечец, помисли си отново той, стана и също напусна опустялата кръчма. Излезе на улицата и за миг се замисли какво да предприеме сега.\r\n\r\nПрибави една точка Познание.\r\n\r\nМожеше да потърси местната шаманка и да се опита да поговори с нея за случващото се - иди на ###54###.\r\n\r\nСъщо така можеше да потърси информация и помощ от кмета на селото - в крайна сметка бе назначен да проведе официално разследване - мини на ###52###.\r\n\r\nБи могъл да поогледа околностите на селото - иди на ###21###.\r\n\r\nА можеше ида се отправикъм къщата на девойката - мини на ###22###.'),
(62, 'Ким Ги Су разбра, че въпросната шаманка се навърта тъдява от около месец. Хьо - бащата на момичето я домъкнал от някъде, надявайки се да помогне на дъщеря му. Старата Гун се държала меко казано странно - вечно си мърморела под носа, издавала странни кашлящи звуци вместо смях, докато се смеела на шегите, които сама си разказвала. Идвала в къщата на Са Гуа по обяд и си тръгвала надвечер, но никой не знаел къде спи, а и никой не умирал от желание да разбере.\r\n\r\nМестните кучета ненавиждали старицата и изпадали в бесен лай щом я надушели, но щом тя се приближавала страхливо подвивали опашки. Може би заради миризмата, която се разнасяла от нея, или заради вида й - старицата била ужасяващо грозна и дори злокобна - човек не би искал да я срещне сам по тъмно. Ги Су разбра още, че местната шаманка - мъдрата Че изобщо не понасяла колегата си и се стремяла да страни от нея.\r\n\r\nАко нямаш кодова дума „шаман“ , прибави точка познание. Сега можеш да отидеш на епизод ###20### и да потърсиш друг начин да събереш още информация. Или би могъл да:\r\n\r\nпроучиш покрайнините на селото - иди на ###21###.\r\n\r\nда се насочиш към къщата на прокълнатата девойка - иди на ###22###.'),
(63, 'Ким Ги Су разбра, че Са Гуа е било мило и срамежливо момиче, което безкрайно обичало майка си. За нещастие тя починала преди две години и смъртта й се отразила много тежко на девойката. Цяла година била покрусена, не излизала от вкъщи, боледувала често.\r\n\r\nМестната шаманка полагала много усилия, за да върне към живота Са Гуа и сякаш успяла, защото точно една година след смъртта на майка си тя започнала отново полека да се връща към себе си, да контактува с хора да излиза от вкъщи. Само че поведението й започнало рязко да се променя. Тя страняла от старите си приятелки, избягвала компанията на жени по принцип, но за сметка на това обръщала голямо внимание на местните младежи, като флиртувала с тях невинно, но с майсторство, на което биха могли да завидят доста кисенг.\r\n\r\nВ същото време тя започнала да се променя и на външен вид - не просто започнала да се превръща в жена, а в невероятна красавица. Чертите й се изменили постепенно до неузнаваемост и съвършенство. Заедно с това нараствали и уменията й във флиртуването и скоро всичко младежи в селото жадували за нея. Не малко от женените мъже също замечтано се заглеждали в нея. Станала причина за доста дрязги и няколко сбивания. Докато първият младеж осмелил се да поиска ръката й не починал.\r\n\r\nПовечето хора твърдяли, че е препил със соджу, но няколко очевидци твърдяли, че езикът му бил почернял, сякаш от силна отрова. Следващият младеж бил намерен удавен в близкото езеро няколко дни след като изчезнал и бил наръфан до неузнаваемост от хищните риби виреещи в него, но някои твърдяли, че вътрешностите му били изтръгнати.\r\n\r\nОт тогава цялата история започнала да се превръща в истерия. Бащата не искал и да чуе за помощ от местната шаманка, обвинявал нея за ставащото и вместо това домъкнал незнайно откъде друга. А кандидат - женихите продължавали да измират. Странно било, че ако преди Са Гуа обожавала кучета, сега изпитвала страх и неприязън към тях. Друго много странно нещо, е че малко след промяната й нейното любимо куче започнало да се държи враждебно с нея, а скоро след това било намерено разкъсано от диви животни.\r\n\r\nАко нямаш кодова дума „опашка“, прибави ###1###т. Познание . Сега можеш да отидеш на епизод ###20### и да потърсиш друг начин да събереш още информация. Или би могъл да:\r\n\r\nпроучиш покрайнините на селото - иди на ###21###.\r\n\r\nда се насочиш към къщата на прокълнатата девойка - иди на ###22###.'),
(64, 'Ким Ги Су научи, че Хьо Уон Джин - бащата на девойката - е уважаван и преуспяващ производител на зелен чай в областта. Познаван е като честен, трезвомислещ човек, който много обичал жена си и дъщеря си.\r\n\r\nСлучило се така, обаче че след завръщането си от поредното пътуване свързано със сделка за продажба на зелен чай, той се върнал водейки една млада жена, която направил прислужница в дома си. Как и къде я бил срещнал било неизвестно. Отначало всичко си продължило по старому, докато един ден жена му решила, че Хьо й изневерява с новата слугиня. Дали обвинението й е било основателно, или не, не се знае, но жената дотолкова се озлобила, че пребила прислужницата в двора на къщата. Хьо едва успял да я озапти. Въпреки това прислужницата умряла на следващата сутрин - говори се, че съпругата на Хьо я отровила, но така и не било доказано.\r\n\r\nНо едно зло води след себе си друго и скоро след случката ревнивата жена заболяла и се споминала. Хьо бил покрусен от ставащото, още повече, че дъщеря му Хьо Са Гуа била много привързана към майка си и се поболяла от мъка. С цената на много грижи и средства той я върнал към живота, но тя била коренно променена. Започнала да се държи предизвикателно с местните младежи, заради нея били предизвикани дрязги и няколко побоя. Най - страшното дошло, когато първият младеж, който поискал ръката й, умрял на следващата сутрин. А не след дълго и вторият. Това довършило каквото било останало от трезвото мислене на Хьо и той, загубил вяра в способностите на местната шаманка, издирил от някъде старата Гун - отвратителна старица, която всеки ден извършвала пречистващи ритуали в къщата му - без никакъв виден успех, защото кандидат - женихите продължават да измират.\r\n\r\nПрибави една точка познание.\r\n\r\nСега можеш да отидеш на епизод ###20### и да потърсиш друг начин да събереш още информация.\r\n\r\nИли би могъл да:\r\n\r\nпроучиш покрайнините на селото - иди на ###21###.\r\n\r\nда се насочиш към къщата на прокълнатата девойка - иди на ###22###.'),
(65, 'Ким Ги Су реши да отдели известно време за изследване на покрайнините на селото. Запъти се към един от терасираните с насаждения от зелен чай хълмове. Ситен дъждец бе започнал да разпръсква полека падналата мъгла и правеше времето още по-задушно. Някъде из бялата пелена се чуваха птичи песни, които правеха пейзажа почти приказен. Ги Су си помисли, че с удоволствие би прекарал старините си на такова място, далеч от шумната столица, отдаден на медитация и покой.\r\n\r\nАко имаш куче - иди на ###23###.\r\n\r\nАко имаш умение „Късмет“ или „Шаманизъм“ и искаш да ги използваш - иди на ###24###.\r\n\r\nАко не - иди на ###26###.'),
(66, 'Докато хуаранг наближаваше езерото се бе спуснала гъста мъгла, която беше обвила всичко в бяла пелена. Той застана на брега и се взря в далечината, където тихите води и мъглата се сливаха. Фееричната гледка, го накара да се почувства като в сън, а зрението му се замъгли от белотата. Звуците бяха приглушени, а въздуха лепкав от влага.\r\n\r\nСтори му се, че през стената от мъгла, вижда някой да крачи на отсрещния бряг на езерото. Запъти се натам, но след секунди се закова на мястото си. Замъглената фигура не беше на отсрещния бряг. Тя се носеше върху мъгливите кълбета над средата на езерото.\r\n\r\nПосле се понесе към него и хуаранг видя че това е мъж облечен в разкъсани, прогизнали дрехи. Докато го приближаваше, Ги Су почувства как космите на тила му се изправят от свръхестествен страх. Лицето на мъжа бе проядено и половината висеше на парцали разлагаща се плът. Кожата му беше сива като а мъртва риба, а там където трябваше да е стомаха му зееше огромна дупка. Вътрешностите липсваха.\r\n\r\nПриближаваша го, докато се носеше на ступка над водата и хуаранг осъзна, че всъщност вижда през него. Привидението протегна ръце към него, сякаш искаше да го сграбчи.\r\n\r\nАко имаш умението „Шаманизъм“ и искаш да го използваш - иди на ###70###.\r\n\r\nАко не би могъл да атакуваш съществото - иди на ###38###.\r\n\r\nИли да изчакаш и да видиш какво ще се случи - иди на ###25###.'),
(67, 'Ги Су стигна до гробището. Беше неестествено тихо, сякаш нищо не се осмеляваше да наруши тихия сън на мъртвите. Падналата мъгла обгръщаше мястото с бели стени и изглеждаше сякаш, това беше неземно светилище извън забързаните проблеми на живота, където времето не означава нищо.\r\n\r\nТой пристъпваше внимателно покрай леглата за вечен покой на споминалите се. Започваше да се пита какво всъщност прави тук. Нима мъртвите можеха да отговорят на въпросите му? Погълнат в подобни мисли беше достигнал един пресен гроб. „Джи Уон Хянг“ - гласеше надписът. Младият човек погребан вчера.\r\n\r\nДочу странен звук зад надгробния камък и се приближи с ръка на меча. Стресна го внезапен плясък на крила и един уплашен гарван отлетя, изпускайки нещо на все още прясно разкопаната пръст на гроба. Беше гривна. Заинтригуван, хуаранг се наведе да я вземе.\r\n\r\nАко имаш умението „Шаманизъм“ и искаш да го използваш - иди на ###30###.\r\n\r\nВ противен случай можеш да вземеш гривната - иди на ###8###.\r\n\r\nИли можеш да оставиш нещо на земята преди да я вдигнеш - иди на ###68###.'),
(68, 'Ги Су почти докосна с пръсти гривната, но изведнъж се сети за нещо важно.\r\n\r\nИди на ###30###.'),
(69, 'Пред него стоеше мъж в средата на двадесетте. Носеше отличителните, макар и странни одежди на шаман и някои от задължителните атрибути в облеклото му липсваха. Нямаше задължителната шаманска шапка, а лявата му ръка беше празна вместо да държи шаманско чукче\r\n-	Казвам се Джо Шин Хо и съм шимбанг - странстващ шаман - представи се непознатия\r\n-	Какво правиш тук\r\n-	Нищо конкретно. Предприел съм своето ритуално пътуване за посвещение. Странствам в търсене на хора, които имат нужда от способностите ми. Трябва да извърша пет успешни шамански ритуала, за да завърша посвещението си и да стана истински шимбанг. Долових голямо зло в тази местност и реших да погледна по-отблизо. Сега обаче виждам, че не аз съм призван да се изправя срещу него. Поне не още. Ти си избраният, хуаранг\r\n-	Хуаранг Ким Ги Су. За мен е чест да те срещна шимбанг и ти желая успех в странството ти за посвещение - Ги Су се поклони уважително, докато се представяше на свой ред, защото шимбанг бяха най-могъщите шамани. Докато обикновените шамани преминавах преа дълъг период на обучение, за да овладеят тайните на шаманизма, шимбанг бяха родени с дарбата. Те нямаха нужда да бъдат обучавани, за да комуникират със света на духовете и на мъртвите. Те живееха едновременно в двата свята. В техния и този на живите\r\n-	За мен е чест да се срещнем, хуаранг Ким Ги Су. Чувствам че бъдещето ни обвързва отново да се срещнем. Дотогава ти пожелавам успех в задачата ти, защото срещу себе си имаш сериозен противник. Духовете не ми позволяват да ти помогна с друго, освен с малък съвет. Бъди безмилостен.\r\n\r\nС тези думи шимбанг пристъпи в мъглата и изчезна. Ги Су осмисляше срещата, когато се сети за гривната. Отвори шепата си и я разгледа. Беше най-обикновена гривна, без особена стойност. Вгледа се и забеляза надпис от вътрешната й страна. Той гласеше „Са Гуа“.\r\n\r\nЗапиши кодова дума „гривна“. Увеличи познанието си с една точка.\r\n\r\nХуаранг се замисли какво да предприеме. Можеше да... се върне в селото и да разпита местните - иди на ###20###. изследва покрайнините - иди на ###21###.\r\n\r\nсе насочи право към къщата на прокълнатата девойка - иди на ###22###.'),
(70, 'Докато инстинктите му крещяха да се защити, съзнанието му нашепваше да стои спокоен. Създанието бе наистина ужасяващо, но внимателното му приближаване не разкриваше зли намерения. То застана неподвижно и след секунда се превърна в красив млад мъж с лице идаващо безкрайна скръб. Той протегна ръка към хуаранг и сякаш зачака. Без да мисли Ги Су протегна своята и докосна призрачната длан. В този миг видението го връхлетя.\r\n\r\nИди на ###28###.'),
(71, 'Извади две точки издръжливост. Ако имаш кодова дума „дрямка“, вече отново рязполагаш с уменията, които си избрал в началото на играта.\r\n\r\nКим Ги Су, с нечовешко усилие на волята, успя да да прочисти част от съзнанието си, отхвърли омаята и и отвори очи. Усети меки устни върху своите, и нежен език, който настойчиво изучаваше вътрешността на устата му. Усещането беше невероятно. Са Гуа! -осъзна той веднага. Стори му се, че езикът й прехвърля нещо обло, твърдо и гладко от нейната уста в неговата. Нещо не беше наред. Този език бе твърде дълъг, за да е човешки, с ужас осъзна хуаранг.\r\n\r\nТой стисна зъби с всичка сила и отхапа върха на въздългия език, задържайки предмета в устата си. Девойката нададе врясък, който определено не беше човешки и инстинктивно се дръпна назад. Ги Су я сграбчи за яката на копринената нощница и разкъса дрехата. Девойката се строполи на земята полугола. Ги Си изплю кървавото парче плът, което стискаше в устата си на земята. Заедно с него се изтърколи малко полирано като перла топче, което светеше с мека зелена светлина.\r\n\r\nВ същия миг тялото на девойката започна да се размива пред очите му, докато тя стенеше от болка на пода, притиснала с ръце устата си, от която се процеждаше струйка кръв. След секунда пред него на пода лежеше огромна бяла лисица с шест дълги опашки, които се мятаха в различни страни от болка. Жълтите очи над покритата с лапи лисича муцуна го гледаха с неприкрита злоба и болка.\r\n\r\nКумихо! - изкрещя ужасен наум Ги Су. Лисица - демон, чиято цел е да изпива жизнените сили на мъжете, които прелъсти! Ето какво умъртвяваше младежите! Същество, чиято цел бе да погълне толкова човешки души, че да придобие полубожествени сили. Само че тази лисица беше с шест опашки, не с девет! Значи все още не бе придобила пълния размер на способностите си.\r\n\r\nДокато мисълта му препускаше бясно, той стъпка, светещото топче на пода, с което изтръгна още един врясък от демона. В същото време изтегли меча си и го опря в гърлото на съществото. Допира на хладната стомана в гърлото й накара лисицата да млъкне\r\n-	Време е да умреш, демоне! - процеди през зъби Ги Су\r\n-	Не! - проговори съществото с познатия глас на Хьо Са Гуа. -Моля те, изслушай ме преди да ме убиеш. Безсилна съм - ти унищожи източника на магията ми - тя кимна към парчетата от топчето. - Моля те да ме изслушаш, а после реши дали заслужавам смърт\r\n-	Говори, но по-бързо! - изсъска хуаранг с отвращение. Това същество бе навряло езика си в устата му\r\n-	Преди три години, когато бях съвсем млада лисица и тепърва започвах да разбирам и овладявам способностите си Хьо Уон Джин ме спаси от сигурна смърт - започна да разказва съществото. - Бях направила първото си превъплъщение в човек, когато попаднах на банда разбойници, които ме нападнаха и щяха да ме изнасилят. Вързаха ръцете ми и запушиха устата ми, така че бях неспособна да се защитавам. Тогава се появи Уон Джин и ги прогони. Дадох клетва да му служа вярно до края на живота му, без да му причинявам зло. Той ме направи прислужница в дома си и аз съвестно работех и се трудех, за да улесня живота на спасителя си. Жена му, обаче започна да ревнува от мен. Не, нищо не бях направила, за да й дам причина, но тя някак усещаше, че не съм това, което изглеждам. Една вечер Уон Джин и аз бяхме останали насаме и си говорехме невинно. Изведнъж в стаята нахлу жена му и като ни видя сами побесня. Нахвърли се върху мен, а когато Уон Джин се опита да я спре, го удари в беса си с една ваза и той загуби съзнание. Не можех да сторя зло на съпругата на спасителя си и се опитах да й обясня, че нищо не се случва между нас. Тя обаче не искаше и да чуе. Преби ме почти до смърт и ако не бяха се притекли на помощ останалите слуги, сигурно това щеше да бъде и краят ми. Същата вечер събрах последните си сили и повиках майка си на помощ. Тя е стара деветоопашата лисица - демон с огромна сила. Побесня, когато ме видя полумъртва. Преди да ме отнесе от тук, за да ме изцери, тя прокле съпругата на Хьо Уон Джин. Казват, че починала след месец. Докато ме лекуваше майка ми ме разпита как съм позволила това да ми се случи. Така научи за Хьо Са Гуа. Един ден се върна след няколкодневно отсъствие и ми каза, че е убила Са Гуа. Накара ме да се превъплатя в нея и заедно да използваме образа й, за да крадем жизнената енергия на кандидат - женихите. Трябваше ми почти година, преди да направя пълно превъплъщението си в нея. Местната шаманка - мъдрата Че не направи задачата ми по - лесна. Тогава майка ми се превъплъти в шаманка - старата Гун и убеди Уон Джин да пропъди старицата и да се възползва от нейните способности. Това е историята ми, хуаранг! Ти прецени заслужавам ли смърт\r\n-	Къде е тялото на Са Гуа, демоне! - изръмжа Ги Су\r\n-	В една пещера под единия от чаените хълмове - отговори създанието и вдигна умолително поглед към Ги Су.\r\n\r\nТой реши да..\r\n\r\n... убие изчадието - иди на ###73###.\r\n\r\n... пощади лисицата-демон, при условие, че двете с майка й напуснат пределите на страната и се закълнат, че никога повече няма да се върнат - иди на ###74###.'),
(72, 'Поради свойствата на опиата във виното, Ги Су не може да разчита на първоначалните си умения. Избери произволно три различни умения от изброените в началото на играта. Не можеш,обаче да се възползваш от допълнителните точки Съзнание и Познание, които са упоменати там. След тези поправки иди на ###50###.'),
(73, 'Ким Ги Су не позволи на молбите за милост на лисицата да го разколебаят\r\n-	Демоне! - изръмжа той. - Макар към теб да са се отнесли несправедливо, това не дава право на теб, или на майка ти, да посегнете на живота на невинно момиче, а после да убивате беззащитни младежи. Колко още живота си отнела тук в тази стая, изчадие?! - изкрещя той и с бързо движение преряза гърлото на лисицата - демон. - Когато съществото се строполи на пода, той видя, че в една от лапите си стиска кинжал. Ето какво му беше готвила като отплата в случай, че бе проявил милост. Иди на ###99###.');
INSERT INTO `episode` (`id`, `text`) VALUES
(74, 'Молбите за милост на лисицата разколебаха Ги Су\r\n-	Демоне! - изръмжа той. - Макар към теб да са се отнесли несправедливо, това не дава право на теб, или на майка ти, да посегнете на живота на невинно момиче, а после да убивате беззащитни младежи. Въпреки това, аз съм готов да пощадя живота ти, ако ти и прокълнатата ти майка се закълнете да напуснете пределите на Шилла и никога повече да не се мярвате тук\r\n-	Заклевам се, благородни хуаранг! Заклевам се! - проплака демонът.\r\n\r\nГи Су свали меча си, но в този миг кумихо с нечовешка бързина се нахвърли върху него с кинжала, който беше скрила в лапата си. Ето каква беше нейната благодарност за проявената милост.\r\n\r\nАко имаш умението рефлекс и имаш нужните точки ки-енергия, за да го използваш - иди на ###75###. Ако не избери - ###76### или ###77###.'),
(75, 'Благодарение на късмета и рефлексите си Ги Су успя да избегне някак удара на коварното създание и кинжала само леко го одраска по ръката. Извади две точки издръжливост. Лисицата изсъска яростно и отскочи назад с изумителна бързина, готвейки се за нова атака.\r\n\r\nАко имаш кодова дума „жар“ - иди на ###80###.\r\n\r\nАко я нямаш, но точките ти за познание са повече от ###4###- иди на ###81###. Ако си майстор на острието и имаш нужните точки ки-енергия, за да използваш това умение - иди на ###78###.\r\n\r\nАко си майстор стрелец и имаш нужните точки ки-енергия, за да използваш това умение - иди на ###79###.\r\n\r\nАко имаш уменията късмет и рефлекс и имаш нужните точки ки-енергия, за да използваш тези умения - иди на ###81###.\r\n\r\nАко не - иди на ###76###.'),
(76, 'Кинжала на девойката-демон се впи в плътта на Ги Су. Той изкрещя някакво проклятие от болка и яростно я отблъсна от себе си. Успя да вдигне меча си и да й пререже гърлото, а после силите го напуснаха и той се отпусна на пода до потреперващото мъртво тяло на кумихо. В същото време в стаята нахлу Уон Джин, който при вида на окървавените тела на пода припадна. Последната мисъл на Ги Су беше, че поне бе отървал селото от демона\r\n- Така милостта на хуаранг Ким ГиСу била възнаградена със смърт -завърши разказа си куангде. Огледа се победоносно, очаквайки дъжд отмонети и аплодисменти, но тълпата муподхвърли едва няколко медни кръгчета и разочаровано се разотиде. Явно вСоУл нямашемясто за него, помисли си тъжно куангде,взе монетите и реши надругиядендапотърсидругград заисториите си.'),
(77, 'Иди на ###75###.'),
(78, 'Благодарение на бойните си умения, Ги Су успя да се противопостави на нечовешката бързина на кумихо. Двамата си разменяха яростни, бързи удари, трудно проследими от окото, разчитайки повече на нюха и инстинкта, отколкото на мисълта и зрението. Цялата схватка продължи не повече от десет секунди, но на Ги Су му се сториха цала вечност. Най- накрая с един последен финт той проби защитата на демона и го промуши с меча си. Животът напусна кумихо с дълъг, протяжен вик\r\n-	Мааайкооо! Иди на ###99###.'),
(79, 'След като успя да избегне пореден удар на кинжала Ги Су направи широка дъга с меча си, принуждавайки кумихо да отскочи доста назад. В края на маха, той използва набраната инерция и запрати меча си към нея като прониза една от лапите й, прковавайки я за стената. Изчадието изквича от болка и задърпа лапата си в опити да я отскубне. В това време хуаранг бе вдигнал лъка си, трескаво извади първата попаднала му стрела и докато демона се хвърляше към него, той стреля от упор. Беше попаднал на стрела - таран, чийто връх тежеше около двеста грама. Тя премина през тялото на лисицата образувайки широка колкото юмрук дупка, проби и стената зад нея, за да се забие в дънера на едно старо овощно дърво в градината.\r\n\r\nЖивотът напусна кумихо с дълъг, протяжен вик. -	Мааайкооо!\r\n\r\nИди на ###99###.'),
(80, 'След като успя да избегне пореден удар на кинжала Ги Су направи широка дъга с меча си, принуждавайки кумихо да отскочи доста назад. В края на маха, той използва набраната инерция и запрати меча си към нея като прониза една от лапите й, прковавайки я за стената. Изчадието изквича от болка и задърпа лапата си в опити да я отскубне. В това време Ги Су сграбчи кожената торбичка дадена му от разбойниците и се хвърли през прозореца в градината, като внимаваше да не се приземи върху нея. Претърколи се през рамо и се изправи. През това време демона бе освободил ръката си и се хвърли след него през прозореца. В същия миг хуаранг хвърли кожената торбичка по кумихо с все сила и я уцели по средата на скока й. Чу се съскащ звук наместо взрив, както му бяха обяснили разбойниците, но след миг тя лумна в пламъци и нададе зверски писък, като се замята насам - натам в нуеспешни опити да потуши пламъците.\r\n\r\nЖивотът напусна кумихо с дълъг, протяжен вик. -	Мааайкооо!\r\n\r\nИди на ###99###.'),
(81, 'Иди на ###78###.'),
(82, 'Ким Ги Су сънуваше. Намираше се в покрайнините на селото, близо до един от чаените хълмове. Неестествено гъста мъгла се спускаше наоколо, разкривайки само няколко метра от заобикалящата го местност. Той вървеше безцелно нанякъде, като сам не знаеше къде. Изведнъж се озова пред местното гробище, където един прясно изкопан гроб привлече вниманието му. Той се приближи и прочете надписа на надгробния камък. „Хуаранг Ким Ги\r\n\r\nСу“. Тръпки на ужас полазиха врата му и той отскочи назад. В същия миг една мъртвешки бяла ръка разора пръстта на гроба с острите си нокти и посегна към него. Той изкрещя от ужас и отскочи още по назад. Нещо го спъна. Друга ръка от съседен гроб се беше впила в крака му. Той я изрита и се изправи. Страховита гледка се разкри пред очите му. От мъглата изплуваха с клатушкащи се тромави походки мъртъвци излезли от местата си на вечен покой. Гротескно разкривените им, проядени от червеи челюсти бяха зинали в нечовешки глад, а разлагащите се ръце се протягаха към него. Ги Су измъкна меча си и се приготви скъпо да продаде живота си.\r\n\r\nЗапиши кодова дума „мъртвец“.\r\n\r\nАко имаш кодова дума „жар“ - иди на ###88###. Ако имаш кодова дума „магия“ - иди на ###89###.\r\n\r\nАко имаш едно от уменията майстор на острието, майстор стрелец или шаманизъм, или две от уменията рефлекс, късмет и самоконтрол и имаш нужната ки-енергия, за да ги използваш - иди на ###85###.\r\n\r\nВ противен случай иди на ###93###.'),
(83, 'Ким Ги Су сънуваше. Намираше се в тясна пещера, с каменен олтар в средата. Странна светлина извираща отникъде, но огряваща навсякъде осветяваше помещението. На олтара имаше нещо. Красива девойка, която го гледаше с широко отворени, мъртви очи. Той тръгна към нея, но с първата му крачка от пода започнаха да изникват и да се прибират остриета на мечове. Той отскочи назад, но в същия миг чу свистене покрай ухото си и разбра, че само късметът му го е спасил от стрелата излетяла от стената вляво от него. Чу грохот над себе си и инстинктивно скочи назад. От тавана се стовари тежък каменен къс на мястото, където бе стоял до преди малко. В същия миг между краката му изникна ново острие.\r\n\r\nЗапиши кодова дума „капан“.\r\n\r\nАко имаш кодова дума „магия“ - иди на ###89###.\r\n\r\nАко имаш умението рефлекс, или две от уменията късмет, самоконтрол, майстор на острието и шаманизъм и имаш нужната ки-енергия, за да ги използваш иди на ###86###.\r\n\r\nВ противен случай - иди на ###94###.'),
(84, 'Ким Ги Су сънуваше. Намираше се пред приказно красив дворец. Похлопа на портата и неочаквано за него, му отвори приказно красива девойка. Благодари му, че я е спасил от тегнещото над нея проклятие и с щастлива усмивка го покани вътре.\r\n\r\nВ двореца нямаше никой друг. Тя отвори една врата и се разкри голяма спалня с огромно меко легло и маса, отрупана с напитки и храни. Те поговориха известно време, след което красавицата го заведе до леглото и започна да го масажира по схванатите рамене. Ръцете и постепенно се спуснаха на гърдите му, тя долепи тяло до него и започна страстно да го целува по врата. Ги Су се задъхваше от възбуда. Тя го събори на леглото и го възседна, събличайки дрехите си, а устните й намериха неговите. Докато примираше от сласт от ласките й той усети, че тя сякаш изпиваше силите му с устни, но той не можеше, а и не искаше да се противопостави. Беше съгласен да умре в прегръдките на тази красавица.\r\n\r\nЗапиши кодова дума „страст“.\r\n\r\nАко имаш кодова дума „магия“ - иди на ###89###.\r\n\r\nАко имаш умението самоконтрол, или две от уменията харизма, шаманизъм, рефлекс и късмет шаманизъм и имаш нужната ки-енергия, за да ги използваш - иди на ###87###. В противен случай - иди на ###42###.'),
(85, 'Ги Су избиваше неуморно с оръжието си живите мъртъвци, а те се изправяха отново и отново. Умората започна да забява ръката му и да отслабва ударите му. Тръпки на отчаяние започнаха да се прокрадват у него. Той изпадна в бяс и атакува отчаяно, надавайки яростни крясъци. Изведнъж всичко свърши и той се върна в реалността.\r\n\r\nИди на ###71###.'),
(86, 'Придвижването на хуаранг приличаше на лудешки танц със смъртта. Избягвайки поредната стрела той се озова до олтара, скочи върху него до мъртвата девойка и... В този момент всичко свърши. Той се отърси от съня и мъчително се върна в реалността.\r\n\r\nИди на ###71###.'),
(87, 'С последните късчета на волята си той успя да отблъсне девойката от себе си и се изправи с вик. Тя с крясък падна от леглото, но вместо красива девойка, от там се изправи отвратително лигаво чудовище, което протегна едно пипало към него. Ги Су изкрещя и посегна към меча си и... изведнъж всичко свърши. Той се откъсна от лапите на кошмара и с мъка се върна в реалността.\r\n\r\nИди на ###71###.'),
(88, 'Ги Су избиваше неуморно с оръжието си живите мъртъвци, а те се изправяха отново и отново. Умората започна да забява ръката му и да отслабва ударите му. Тръпки на отчаяние започнаха да се прокрадват у него. Той изпадна в бяс и атакува отчаяно, надавайки яростни крясъци. Отвори пространство пред себе си и пъргаво се изкатери на един огромен камък оставайки ходещите трупове на около два метра под себе си. Нещо издуваше джоба му. Той бръкна вътре и откри кожената торбичка, дадена му от разбойниците. Слава на Буда, имаше я и в съня си. Немъртвите се бяха скупчили под камъка и се катереха един през друг в опити да го докопат. Без колебание той запрати с все сила торбичката по купчината разлагащи се тела и залегна. Чу се страхотен взрив и изведнъж всичко свърши и той се върна в реалността.\r\n\r\nИди на ###71###.'),
(89, 'В отчаянието си Ги Су си припомни за заклинанието на мъдрата Че и го изрече в скоропоговорка. Нищо не се промени. В следващия миг обаче, той беше изтръгнат от лапите на съня и с мъка се върна в реалността.\r\n\r\nИди на ###71###.'),
(90, 'Изведнъж бесен кучешки лай изтръгна Ким Ги Су от лапите на кошмарния унес. Верният му другар се беше върнал, въпреки очевидния си страх и сега лаеше побеснял под прозореца на стаята му. Скоро Ги Су разбра, че животното бе спасило живота му.\r\n\r\nИди на ###71###.'),
(91, 'Силите на Ги Су не стигнаха и той се предаде. В просъница усети един нежен език, който изучаваше устата му и прехвърляше нещо обло, гладко и твърдо навътре и навън, навътре и навън, а с всяко излизане го напускаше и част от жизнената му сила. Нежен топъл език. Твърде дълъг, за да бъде човешки, помисли си хуаранг, а после тъмнината го обгърна.\r\n\r\nКумихо Са Гуа изтегли и последната капка енергия от бездиханния хуаранг и доволно се изкикоти. Какво огромно количество жизнена сила бе получила от поредния глупак\r\n- Така загинал Ким Ги Су, смелият, но глупав, доверчив исластолюбив хуаранг, койтонеразбрал,чекумихоеубиладевойката исеевъплътила внейнияобраз,за да пие душите на глупиви мъже като него -завършиисториятасикуангдеи се огледа триумфално,очаквайки дъжд от аплодисменти и монети. Вместо това, обаче към него полетя дъжд отяйца и развален зарзават.\r\n\r\nКрай.'),
(92, 'Намали точките си за съзнание с една. Ако те станат нула приключението свършва за теб.\r\n\r\nАко имаш кодова дума „сън“ , задраскай избраните умения и избери нови три, като изключиш от избора ползваните досега, ограниченията за точките важат.\r\n\r\nАко имаш кодова дума “страст“ - избери на кой епизод да продължиш ###82### или ###83###.\r\n\r\nАко имаш кодова дума “капан“ - избери на кой епизод да продължиш ###82### или ###84###.\r\n\r\nАко имаш кодова дума “мъртвец“ - избери на кой епизод да продължиш ###84### или ###83###.\r\n\r\nАко имаш кодови думи “страст“ и капан“ - иди на ###82###. Ако имаш кодови думи “страст“ и мъртвец“ - иди на ###83###.\r\n\r\nАко имаш кодови думи “капан“ и “мъртвец“ - иди на ###84###. Ако имаш и трите кодови думи - иди на ###51###.'),
(93, 'Ги Су избиваше неуморно с оръжието си живите мъртъвци, а те се изправяха отново и отново. Умората започна да забява ръката му и да отслабва ударите му. Тръпки на отчаяние започнаха да се прокрадват у него. Той изпадна в бяс и атакува отчаяно, надавайки яростни крясъци. Скоро обаче силите му свършиха съвсем и живите трупове изтръгнаха оръжието от ръката му. Започнаха да го налагат с разлагащи се юмруци и когато го обезсилиха съвсем го понесоха към прясно изкопания гроб. Той изкрещя докато го стоварваха в него. После дойде тъмнината.\r\n\r\nИди на ###92###.'),
(94, 'Придвижването на хуаранг приличаше на лудешки танц със смъртта. Миг преди да достигне олтара обаче чу познатия вече шум на падащ каменен къс и осъзна, че този път няма да може да го избегне. Миг преди скалата да се стовари върху него дойде тъмнината.\r\n\r\nИди на ###92###.'),
(95, 'Странно но при вида на старицата, Ги Су предпочете да остане добре прикрит зад камъка. Тя беше отвратително грозна и се държеше направо зловещо, клатейки се и мърморейки под нос. Той напрегна слух\r\n-	Кяф! Мирише на човек. Някой май се е навъртал тъдява! Кяф! Поредният глупак, който да ми даде душата си! - старицата навири нос и подуши въздуха. За миг погледа й се стрелна в посока на хуаранг, но скоро отмина\r\n-	Така, така! Много добре! Още малко и ще мога да захвърля облика на тази отвратителна старица! - мърмореше си тя под нос, докато вървеше към входа на пещерата. Намъкна се вътре с изненадваща пъргавина и Ги Су много предпазливо се доближи до входа на пещерата, като напрягаше слуха си\r\n-	Я да видим сега що за вкус ще има душичката на поредния кандидат - жених? Кяф, кяф!\r\n\r\nГи Су изчака малко и много внимателно надникна в дупката. За негова изненада нещо осветяваше вътрешността на пещерата. Старицата бе застанала пред олтара с гръб към хуаранг, а на самия олтар беше поставила някакво голямо колкото жълъд топче, от което струеше мека светлина, която сякаш се вливаше в устата на демона.\r\n\r\nОчевидно това същество използваше девойката, която уж лекуваше, за да се сдобива с жизнената енергия на душите на кандидат - женихите.\r\n\r\nДобави една точка Познание. Запиши кодова дума „шаман“. Въоръжен с това знание Ким Ги Су реши да ...\r\n\r\nнападне лисицата - демон тук и сега - иди на ###50###.\r\n\r\nпоразпита местните жители, освен ако вече не го беше направил - иди на ###20###.\r\n\r\nсе насочи към къщата на прокълнатата девойка - иди на ###22###.\r\n\r\nако нямаш кодова дума „гривна“, можеш да отидеш до близкото езеро, освен ако вече не си бил там - иди на ###66###.\r\n\r\nако нямаш кодова дума „вода“, можеш да посетиш местното гробище - иди на ###67###.'),
(96, '-	Е, нали искаше парите и оръжието ми - получи ги. Искаш ли още нещо? - попита Ким Ги Су без следа от веселост в гласа си\r\n-	Ами, виждате ли господине, ъъъ, май е станала грешка. Ъъъ, такова, взехме ви за бандит. Тука, виждате ли, чухме се навъртат такива и, ъъъ, таковата... ами с момчетата решихме да ги посплашим. Ъъъ, така де, да им покажем колко е лошо да се плашат така почтените пътници и хората от селото наблизо. Ама се пообъркахме, май.\r\n\r\nОстаналите закимаха толкова енергично, че щяха да им се схванат вратовете\r\n-	Ахаа! Сега разбирам! Значи според вас аз съм бандит, така ли! Долен разбойник! А! - кресна Ги Су\r\n-	Нннне! Не, не, не, не,не,не,не! Нищо такова господине, от тревата ннне ви видяхме добре, пък и аз съм късоглед и кривоглед! - отвърна онзи и демонстративно събра очи, сякаш забелязал брадавица на върха на носа си\r\n-	Вввижте, изтървахте си париците, без да искате, ъъъ, моля ви махнете меча, за да ви помогна да ги съберем,а! Моля ви! -главатарят продължи да гледа върха на носа си.\r\n\r\nГи Су отдръпна меча си и онзи се просна на земята и започна да събира жълтиците\r\n-	Бяха точно тридесет и две - информира той паникьосания разбойник.\r\n\r\nОтне му точно десет секунди да събере парите, да ги почисти от туфите трева, изскубнати в бързината и да ги пусне в кесията му с треперещи пръсти, сякаш монетите бяха нажежени\r\n-	Ето, добри ми господине! Заповядайте! Извинете ни още веднъж! Пуснете ни да си ходим сега, а! - каза онзи и започна бавно и внимателно да отстъпва назад. Ги Су го остави да направи две крачки и извика\r\n-	Стой!\r\n\r\nОнзи се закова на място\r\n-	Ти не каза ли, че си кривоглед\r\n-	Аммми, аз всъщност гледах настрани господине! - бандитът започна да трака със зъби от страх.\r\n\r\nГи Су вече едва се удържаше да не прихне\r\n-	Само исках да ви кажа момчета, че аз съм хуаранг Ким Ги Су - сигурно сте чували за мен. Та, понеже ми се отвори малко работа в съседното село, ще се навъртам наоколо. Затова няма нужда да се тревожите за онези бандити - сега като ми казахте, лично ще ги потърся, за да срещнат справедливото си наказание. Така че можете спокойно да се прибирате по домовете си. А ако случайно срещнете онези злосторници, не се занимавайте с тях, само им кажете, че ако им е мил животът, е добре да се ометат от тук, колкото се може по-скоро, защото аз идвам за тях\r\n-	Ддда, смели хуаранг! Ттакка и ще направим! Нали момчета? -	Да\r\n-	Да! - изви се хор от разтреперани, но настойчиви гласове. -	Браво на вас! - каза Ги Су\r\n-	Ъъъ, господине,ъъъ, такова... -	Да? - подкани го Ги Су\r\n-	Ами , ние, ъъъ, искаме да ви помогнем във вашето благородно начинание и, ъъъ, в знак на извинение да ви дадем този скромен дар!\r\n\r\nАко имаш умението Късмет и искаш да го използваш - иди на ###10###. В противен случай - иди на ###11###.'),
(97, 'Разбойниците го дариха с погледи на див ужас и хукнаха да бягат, на където им видят очите. Ги Су се обърна към поваления противник, но и той вече препускаше, накуцвайки и крещеше колкото му глас държи “ДЕЕЕМОООООН”.\r\n\r\nСтарчето, от своя страна, си седеше с вдигнати ръце и гледаше изблещен към хуаранг. Ги Су прибра оръжието си и пристъпи към монаха\r\n-	Не се приближавай, демоне! - изкрещя монахът и отскочи около метър назад. Върни се в преизподнята, от която си изпълзяло, изчадие\r\n-	Уважаеми монахо, аз съм хуаранг Ким Ги Су..\r\n-	Стой на място! - изкряка старика. - Ким Ги Су - не съм чувал за такъв демон\r\n-	Аз не съм демон, старче! - отвърна обидено Ги Су. - Аз съм хуаранг Ким Ги Су! - натърти рицарят\r\n-	Я се изплези! -	Моля?\r\n-	Изплези се! - тросна се монахът. - Ако си демон езика ти ще е лилав!\r\n\r\nКим Ги Су внимателно се огледа и след като се увери, че наоколо няма никой, който да стане свидетел на унижението му злобно се изплези на щурия старец\r\n-	Ето! Доволен ли си? - попита Ги Су. Старецът от своя страна го изгледа още по - изблещен, с отворена уста и примигна няколко пъти\r\n-	А аз си помислих, че заклинанието ми е проработило. -смутолеви разочаровано монаха и отпусна ръце\r\n-	Минавах наблизо, чух виковете ти и реших да ти помогна, както изисква честта ми и кодекса на хуаранг. Защо ме обиждаш така?\r\n\r\nМонахът поизпъна дрехите си в не особено успешен опит да възвърне част от загубеното си самообладание\r\n-	Благородни хуаранг! - поклони се ниско той. - Аз съм Пак Йон Уол - странстващ монах - чародей, вещ в тайните на езотериката...или явно не чак толкова вещ. Приеми моите извинения, затова че те... така де...тия разбойници ме поизплашиха малко и ...не бях на себе си преди малко и затова... - стареца дори успя да се изчерви от неудобство\r\n-	Така, де! ...\r\n\r\nАко имаш кодова дума „кражба“ - иди на ###17###.\r\n\r\nАко я нямаш, но имаш и искаш да използващ умението Късмет - иди на ###18###.\r\n\r\nВ противен случай - иди на ###19###.'),
(98, 'Хуаранг извади меча си и замахна към призрака. Острието премина през него сякаш сечеше въздух, без да нанесе никакви поражения. Съществото остана неподвижно за секунда, после изражението му се изкриви заплашително. Преди Ги Су да успеее да реагира то заби палци в слепоочията му. Хуаранг би се строполил на земята от последвалата болка, но неможеше да мръдне. После видението го връхлетя.\r\n\r\nНамали съзнанието си с една точка. Намали издръжливостта си с две точки.\r\n\r\nИди на ###28###.'),
(99, 'В това време нахлу Хьо Уон Джин и при вида на мъртвото същество на пода припадна. Ги Су свести горкия човек и му разказа за случилото се. Мъжът не беше на себе си от мъка\r\n-	О, Хананим! Небесни Боже! Нима сам съм поканил злото у дома си! Дъще, жено! Ще ми простите ли някога!\r\n\r\nСлед като се посъвзе той събра мъжете от селото и заедно откриха пещерата с останките на Са Гуа. Претърсиха цялата околност в търсене на старата Гун - деветоопашатата кумихо, но от нея нямаше и следа.\r\n\r\nВъпреки успеха си Ги Су не беше в настроение за празненства, а и селяните потресени от личната драма на Уон Джин, нямаха и намерение да организират такива.\r\n\r\nРано сутринта Ким Ги Су пое обратния път към столицата, за да докладва на магистъра за случилото се и да му съобщи, че за съжаление, или щастие, на сина му ще се наложи да си търси друга възлюбена.\r\n\r\nТака смелият хуаранг Ким Ги Су отървал село Йо Су от напастта на кумихо и сезавърнал като герой в столицата. -довършваше разказаси куангде, сред притаената тълпа. -Селяните затворили тялото на мъртвата кумихо в същата пещера, в която с майкай заровили невинната Са Гуа и оттози ден нататък село Йо Су се преименовало вЛисича Бърлога. - Тълпата нададе изненадани възгласи,тъй катотова бе истинско населено място на околопетдни пътот столицата.- Всъщото времепо пътя към столицата куцукала и една ужасяващо грозна старица. -продължи куангде. - „Бъди проклет, хуаранг!“ -кълнялатябезспир.„Бъдипроклет.“.„Тимиотне дъщерята-най-скъпото ми! Затова бъди проклет! Сега аз ще отнема най - скъпото за теб - честта ти!“. -публиката бе затаиладъх вочакване\r\n-	Това, обаче е другаистория. -разнесоха се разочаровани възгласи. -Но ако тази ви е харесала-покажете ми го сега и утре пак ще бъда тук, зада ви разкажа как старата кумихо се првъплатила в кралицата на Шилла, как убилакраляихвърлиластранатав смут.ИкаксмелияхуарангКим Ги Су, след като едва сеспасил, извършил най -голямото си геройство и заедно с мъдрия монах Пак Йон Уолосвободилстраната отигото на демона - кумихо!\r\n\r\nВълна от аплодисменти и възторжени викове заля куангде и към него полетя дъжд отмонети. Тук щеше да забогатее.\r\n\r\nКрай.'),
(100, 'Ги Су се наведе да вдигне гривната, но щом я докосна, внезапна болка прониза ръката му. Сякаш студени железни пръсти се бяха впили в нея с достаъчно мощ, за да я разтрошат. Опита да се отдръпне, но не можа да помръдне. Беше парализиран. Болката продължаваше да се усилва и той започна да се тресе в агония.\r\n\r\nТогава почувства друга ръка на рамото си и видя през насълзениет си очи как някой оставя нещо на гроба. Болката внезапно спря. Той се строполи на колене запъхтян\r\n-	Виждам, че не знаеш как да се отнасяш с мъртвите - каза непознатият\r\n-	Първо даваш, а после вземаш от тях. Обидиш ли ги, те се обръщат срещу теб. Би трябвало да знаеш това, хуаранг\r\n-	Кой си ти?\r\n\r\nНамали точките си за съзнание с една и точките си за издръжливост с две.\r\n\r\nИди на ###69###.\r\n\r\n');
